INSERT INTO `tbl_passage` VALUES (4595, 112, '（九）试验医疗器械的研制符合适用的医疗器械质量管理体系相关要求的声明；');
INSERT INTO `tbl_passage` VALUES (4596, 112, '（十）与伦理审查相关的其他文件。');
INSERT INTO `tbl_passage` VALUES (4597, 112, '第十二条  伦理委员会应当对医疗器械临床试验的伦理性和科学性进行审查，并应当重点关注下列内容：');
