INSERT INTO `tbl_passage` VALUES (4619, 112, '（八）适用时，说明受试者可能被分配到临床试验的不同组别；');
INSERT INTO `tbl_passage` VALUES (4620, 112, '（九）受试者参加临床试验是自愿的，且在临床试验的任何阶段有权退出而不会受到歧视或者报复，其医疗待遇与权益不受影响；');
INSERT INTO `tbl_passage` VALUES (4621, 112, '（十）告知受试者参加临床试验的个人资料属于保密，但医疗器械临床试验机构管理部门、伦理委员会、药品监督管理部门、卫生健康管理部门或者监查员、稽查员在工作需要时按照规定程序可以查阅受试者参加临床试验的个人资料；');
INSERT INTO `tbl_passage` VALUES (4622, 112, '（十一）受试者在临床试验期间可能获得的免费诊疗项目和其他相关补偿；');
INSERT INTO `tbl_passage` VALUES (4623, 112, '（十二）如发生与临床试验相关的伤害，受试者可以获得的治疗和/或赔偿；');
INSERT INTO `tbl_passage` VALUES (4624, 112, '（十三）受试者在临床试验期间可以随时了解与其相关的信息资料。知情同意书应当注明制定的版本和日期或者修订后的版本和日期。知情同意书应当采用受试者能够理解的语言和文字。知情同意书不应当含有会引起受试者放弃合法权益以及免除医疗器械临床试验机构和主要研究者、申办者应当负责任的内容。');
INSERT INTO `tbl_passage` VALUES (4625, 112, '第十五条  伦理委员会的跟踪审查：');
INSERT INTO `tbl_passage` VALUES (4626, 112, '（一）伦理委员会应当对医疗器械临床试验进行跟踪监督，发现受试者权益和安全不能得到保障等情形，可以在任何时间书面要求暂停或者终止该项临床试验；');
INSERT INTO `tbl_passage` VALUES (4627, 112, '（二）伦理委员会需要审查研究者报告的本临床试验机构发生的严重不良事件等安全性信息，审查申办者报告的试验医疗器械相关严重不良事件等安全性信息。伦理委员会可以要求修改临床试验方案、知情同意书和其他提供给受试者的信息，暂停或者终止该项临床试验；');
INSERT INTO `tbl_passage` VALUES (4628, 112, '（三）伦理委员会需要审查临床试验方案的偏离对受试者权益和安全的可能影响，或者对医疗器械临床试验的科学性、完整性的可能影响。');
INSERT INTO `tbl_passage` VALUES (4629, 112, '第十六条  医疗器械临床试验过程中，修订临床试验方案以及知情同意书等文件、恢复已暂停的临床试验，应当在重新获得伦理委员会的书面同意后方可实施。');
INSERT INTO `tbl_passage` VALUES (4630, 112, '第十七条  伦理委员会应当保存伦理审查的全部记录，包括伦理审查的书面记录、委员信息、递交的文件、会议记录和相关往来记录等。第三章  医疗器械临床试验机构');
INSERT INTO `tbl_passage` VALUES (4631, 112, '第十八条  医疗器械临床试验机构应当符合备案条件，建立临床试验管理组织架构和管理制度。医疗器械临床试验机构应当具有相应的临床试验管理部门，承担医疗器械临床试验的管理工作。');
INSERT INTO `tbl_passage` VALUES (4632, 112, '第十九条  医疗器械临床试验机构管理部门应当负责在医疗器械临床试验机构备案管理信息系统中填报、管理和变更医疗器械临床试验机构备案信息，包括临床试验专业、主要研究者等信息；负责在备案系统中在线提交上一年度实施医疗器械临床试验工作总结报告；负责在伦理委员会对医疗器械临床试验审查前，组织评估该临床试验主要研究者的资质并完成其备案。');
INSERT INTO `tbl_passage` VALUES (4633, 112, '第二十条  医疗器械临床试验机构应当建立质量管理制度，涵盖医疗器械临床试验实施的全过程，包括培训和考核、临床试验的实施、医疗器械的管理、生物样本的管理、不良事件和器械缺陷的处理以及安全性信息的报告、记录、质量控制等制度，确保主要研究者履行其临床试验相关职责，保证受试者得到妥善的医疗处理，确保试验产生数据的真实性。');
INSERT INTO `tbl_passage` VALUES (4634, 112, '第二十一条  医疗器械临床试验机构在接受医疗器械临床试验前，应当根据试验医疗器械的特性评估相关资源，确保具备相匹配的资质、人员、设施、条件等。');
INSERT INTO `tbl_passage` VALUES (4635, 112, '第二十二条  医疗器械临床试验机构和研究者应当配合申办者组织的监查和稽查，以及药品监督管理部门、卫生健康管理部门开展的检查。');
INSERT INTO `tbl_passage` VALUES (4636, 112, '第二十三条  医疗器械临床试验机构应当按照相关法律法规和与申办者的合同，妥善保存临床试验记录和基本文件。第四章  研究者');
INSERT INTO `tbl_passage` VALUES (4637, 112, '第二十四条  负责医疗器械临床试验的主要研究者应当具备下列条件：');
INSERT INTO `tbl_passage` VALUES (4638, 112, '（一）已完成医疗器械临床试验主要研究者备案；');
INSERT INTO `tbl_passage` VALUES (4639, 112, '（二）熟悉本规范和相关法律法规；');
INSERT INTO `tbl_passage` VALUES (4640, 112, '（三）具有试验医疗器械使用所要求的专业知识和经验，经过临床试验相关培训，有临床试验的经验，熟悉申办者所提供的医疗器械临床试验方案、研究者手册等资料；');
INSERT INTO `tbl_passage` VALUES (4641, 112, '（四）有能力协调、支配和使用进行该项医疗器械临床试验的人员和设备，且有能力处理医疗器械临床试验中发生的不良事件和其他关联事件。');
INSERT INTO `tbl_passage` VALUES (4642, 112, '第二十五条  主要研究者应当确保医疗器械临床试验遵守伦理委员会同意的最新版本临床试验方案；在约定的时限内，按照本规范和相关法律法规的规定实施医疗器械临床试验。');
INSERT INTO `tbl_passage` VALUES (4643, 112, '第二十六条  主要研究者可以根据医疗器械临床试验的需要，授权经过临床试验相关培训的研究者，组织进行受试者招募和知情同意、筛选和随访；试验医疗器械和对照医疗器械（如适用）的管理和使用；生物样本的管理和使用（如适用）；不良事件和器械缺陷的处理；临床试验数据记录以及病例报告表填写等。');
INSERT INTO `tbl_passage` VALUES (4644, 112, '第二十七条  参与医疗器械临床试验的研究者应当：');
INSERT INTO `tbl_passage` VALUES (4645, 112, '（一）具有承担医疗器械临床试验相应的专业技术资格、培训经历和相关经验；');
INSERT INTO `tbl_passage` VALUES (4646, 112, '（二）参加申办者组织的与该医疗器械临床试验相关的培训，并在主要研究者授权的范围内参与医疗器械临床试验；');
INSERT INTO `tbl_passage` VALUES (4647, 112, '（三）熟悉试验医疗器械的原理、适用范围或者预期用途、产品性能、操作方法、安装要求以及技术指标等，了解该试验医疗器械临床前研究相关资料；');
INSERT INTO `tbl_passage` VALUES (4648, 112, '（四）充分了解并且遵守临床试验方案、本规范和相关法律法规规定以及与医疗器械临床试验相关的职责；');
INSERT INTO `tbl_passage` VALUES (4649, 112, '（五）掌握临床试验可能产生风险的防范以及紧急处理方法。');
INSERT INTO `tbl_passage` VALUES (4650, 112, '第二十八条  研究者应当遵守《世界医学大会赫尔辛基宣言》的伦理准则及相关伦理要求，并符合以下要求：');
INSERT INTO `tbl_passage` VALUES (4651, 112, '（一）应当使用经伦理委员会同意的最新版本知情同意书和其他提供给受试者的信息；');
INSERT INTO `tbl_passage` VALUES (4652, 112, '（二）在受试者参与临床试验前，应当向受试者说明试验医疗器械以及临床试验有关的详细情况，告知受试者可能的受益和已知的、可以预见的风险，经充分和详细解释后由受试者在知情同意书上签署姓名和日期，研究者在知情同意书上应当签署姓名和日期；');
INSERT INTO `tbl_passage` VALUES (4653, 112, '（三）受试者为无民事行为能力人或者限制民事行为能力人的，应当依法获得其监护人的书面知情同意；受试者缺乏阅读能力的，应当有一位公正见证人见证整个知情同意过程并在知情同意书上签字并注明日期；');
INSERT INTO `tbl_passage` VALUES (4654, 112, '（四）不应当强迫或者以其他不正当方式诱使受试者参加临床试验；');
INSERT INTO `tbl_passage` VALUES (4655, 112, '（五）确保知情同意书更新并获得伦理委员会审查同意后，所有受影响的未结束试验流程的受试者，都签署新修订的知情同意书。');
INSERT INTO `tbl_passage` VALUES (4656, 112, '第二十九条  研究者对申办者提供的试验医疗器械和对照医疗器械（如适用）有管理责任，应当确保其仅用于参加该医疗器械临床试验的受试者，在临床试验期间按照要求储存和保管，在临床试验完成或者终止后按照相关法律法规和与申办者的合同进行处理。');
INSERT INTO `tbl_passage` VALUES (4657, 112, '第三十条  研究者应当确保医疗器械临床试验中生物样本的采集、处理、保存、运输、销毁等符合临床试验方案和相关法律法规。');
INSERT INTO `tbl_passage` VALUES (4658, 112, '第三十一条  医疗器械临床试验中发生不良事件时，研究者应当为受试者提供足够、及时的治疗和处理；当受试者出现并发疾病需要治疗和处理时，研究者应当及时告知受试者。研究者应当记录医疗器械临床试验过程中发生的不良事件和发现的器械缺陷。');
INSERT INTO `tbl_passage` VALUES (4659, 112, '第三十二条  研究者应当及时报告医疗器械临床试验中的安全性信息：');
INSERT INTO `tbl_passage` VALUES (4660, 112, '（一）医疗器械临床试验中发生严重不良事件时，研究者应当立即对受试者采取适当的治疗措施；同时，研究者应当在获知严重不良事件后24小时内，向申办者、医疗器械临床试验机构管理部门、伦理委员会报告；并按照临床试验方案的规定随访严重不良事件，提交严重不良事件随访报告；');
INSERT INTO `tbl_passage` VALUES (4661, 112, '（二）发现医疗器械临床试验的风险超过可能的受益,需要暂停或者终止临床试验时，主要研究者应当向申办者、医疗器械临床试验机构管理部门、伦理委员会报告,及时通知受试者，并保证受试者得到适当治疗和随访。');
INSERT INTO `tbl_passage` VALUES (4662, 112, '第三十三条  主要研究者应当对收到的安全性信息及时处理：');
INSERT INTO `tbl_passage` VALUES (4663, 112, '（一）收到申办者提供的试验医疗器械相关严重不良事件和其他安全性信息时，应当及时签收阅读，并考虑受试者的治疗是否进行相应调整，必要时尽早与受试者沟通；');
INSERT INTO `tbl_passage` VALUES (4664, 112, '（二）收到申办者或者伦理委员会需要暂停或者终止医疗器械临床试验的通知时，应当及时通知受试者，并保证受试者得到适当治疗和随访。');
INSERT INTO `tbl_passage` VALUES (4665, 112, '第三十四条  主要研究者应当按时向伦理委员会报告医疗器械临床试验的进展，及时报告影响受试者权益和安全的事件或者对临床试验方案的偏离。');
INSERT INTO `tbl_passage` VALUES (4666, 112, '第三十五条  医疗器械临床试验机构和研究者对申办者严重或者持续违反本规范和相关法律法规，或者要求改变试验数据、结论的行为，应当书面向申办者所在地省、自治区、直辖市药品监督管理部门报告。第五章  申办者');
INSERT INTO `tbl_passage` VALUES (4667, 112, '第三十六条  申办者应当对医疗器械临床试验的真实性、合规性负责。申办者为境外机构的，应当按照相关法律法规指定中国境内的企业法人作为代理人，由代理人协助申办者履行职责。');
INSERT INTO `tbl_passage` VALUES (4668, 112, '第三十七条  申办者的质量管理体系应当覆盖医疗器械临床试验的全过程，包括医疗器械临床试验机构和主要研究者的选择、临床试验方案的设计、医疗器械临床试验的实施、记录、结果报告和文件归档等。申办者的质量管理措施应当与临床试验的风险相适应。');
INSERT INTO `tbl_passage` VALUES (4669, 112, '第三十八条  申办者发起医疗器械临床试验前应当：');
INSERT INTO `tbl_passage` VALUES (4670, 112, '（一）确保产品设计已定型，完成试验医疗器械的临床前研究，包括性能验证以及确认、基于产品技术要求的产品检验报告、风险受益分析等，且结果应当能够支持该项医疗器械临床试验；');
INSERT INTO `tbl_passage` VALUES (4671, 112, '（二）根据试验医疗器械的特性，选择已备案的医疗器械临床试验机构、专业和主要研究者；');
INSERT INTO `tbl_passage` VALUES (4672, 112, '（三）负责组织制定研究者手册、临床试验方案、知情同意书、病例报告表、标准操作规程以及其他相关文件，并向医疗器械临床试验机构和主要研究者提供。');
INSERT INTO `tbl_passage` VALUES (4673, 112, '第三十九条  申办者应当与医疗器械临床试验机构和主要研究者签订合同，明确各方在医疗器械临床试验中的权利和义务。');
INSERT INTO `tbl_passage` VALUES (4674, 112, '第四十条  申办者应当在医疗器械临床试验经伦理审查通过并且与医疗器械临床试验机构签订合同后，向申办者所在地省、自治区、直辖市药品监督管理部门进行临床试验项目备案。医疗器械临床试验备案完成后，该医疗器械临床试验机构方可开始第一例受试者知情同意以及筛选。');
INSERT INTO `tbl_passage` VALUES (4675, 112, '第四十一条  医疗器械临床试验开始前，申办者应当负责组织与该医疗器械临床试验相关的培训，如试验医疗器械的原理、适用范围、产品性能、操作方法、安装要求、技术指标以及临床试验方案、标准操作规程以及其他相关文件等。');
INSERT INTO `tbl_passage` VALUES (4676, 112, '第四十二条  申办者应当免费提供试验医疗器械，并符合以下要求：');
INSERT INTO `tbl_passage` VALUES (4677, 112, '（一）试验医疗器械应当按照医疗器械生产质量管理规范的相关要求生产且质量合格；');
INSERT INTO `tbl_passage` VALUES (4678, 112, '（二）确定试验医疗器械的运输条件、储存条件、储存时间、有效期等；');
INSERT INTO `tbl_passage` VALUES (4679, 112, '（三）试验医疗器械应当按照临床试验方案要求进行适当包装和保存；包装标签上应当标明产品信息，具有易于识别、正确编码的标识，标明仅用于医疗器械临床试验；');
INSERT INTO `tbl_passage` VALUES (4680, 112, '（四）医疗器械临床试验获得伦理委员会同意后，申办者负责在规定的条件下将试验医疗器械运输至医疗器械临床试验机构；');
INSERT INTO `tbl_passage` VALUES (4681, 112, '（五）对从医疗器械临床试验机构回收的试验医疗器械，申办者负责保存回收处置等记录。');
INSERT INTO `tbl_passage` VALUES (4682, 112, '第四十三条  申办者应当为受试者支付与医疗器械临床试验相关的费用。受试者发生与医疗器械临床试验相关的损害或者死亡时，申办者应当承担相应的治疗费用、补偿或者赔偿，但不包括研究者和医疗器械临床试验机构自身过失以及受试者自身疾病进展所致的损害。');
INSERT INTO `tbl_passage` VALUES (4683, 112, '第四十四条  申办者应当负责医疗器械试验期间安全性信息的评估和报告：');
INSERT INTO `tbl_passage` VALUES (4684, 112, '（一）申办者应当在获知死亡或者危及生命的临床试验医疗器械相关严重不良事件后7日内、获知非死亡或者非危及生命的试验医疗器械相关严重不良事件和其他严重安全性风险信息后15日内，向参与临床试验的其他医疗器械临床试验机构、伦理委员会以及主要研究者报告，向申办者所在地省、自治区、直辖市药品监督管理部门报告，向医疗器械临床试验机构所在地省、自治区、直辖市药品监督管理部门和卫生健康管理部门报告，并采取风险控制措施；出现可能影响受试者安全、可能影响医疗器械临床试验实施、可能改变伦理委员会同意意见的信息时，应当及时组织对临床试验方案、知情同意书和其他提供给受试者的信息、以及其他相关文件进行修改，并提交伦理委员会审查；');
INSERT INTO `tbl_passage` VALUES (4685, 112, '（二）出现大范围临床试验医疗器械相关严重不良事件，或者其他重大安全性问题时，申办者应当暂停或者终止医疗器械临床试验，并向所有医疗器械临床试验机构管理部门、伦理委员会以及主要研究者报告，向申办者所在地省、自治区、直辖市药品监督管理部门报告，向所有医疗器械临床试验机构所在地省、自治区、直辖市药品监督管理部门和卫生健康管理部门报告。');
INSERT INTO `tbl_passage` VALUES (4686, 112, '第四十五条  申办者应当承担医疗器械临床试验监查责任，制定监查标准操作规程，并选择符合要求的监查员履行监查职责：');
INSERT INTO `tbl_passage` VALUES (4687, 112, '（一）监查员人数以及监查次数应当与医疗器械临床试验的复杂程度和参与临床试验的医疗器械临床试验机构数量相匹配；');
INSERT INTO `tbl_passage` VALUES (4688, 112, '（二）监查员应当受过相应的培训，熟悉本规范和相关法律法规，具备相关专业背景知识，熟悉试验医疗器械的相关研究资料和同类产品临床方面的信息、临床试验方案以及其相关的文件，能够有效履行监查职责；');
INSERT INTO `tbl_passage` VALUES (4689, 112, '（三）监查员应当遵守由申办者制定的监查标准操作规程，督促医疗器械临床试验按照临床试验方案实施。监查的内容包括医疗器械临床试验机构和研究者在临床试验实施过程中对临床试验方案、本规范和相关法律法规的依从性；受试者知情同意书签署、筛选、随访、权益和安全保障；试验医疗器械和对照医疗器械（如适用）的管理和使用；生物样本的管理和使用（如适用）；不良事件和器械缺陷的处理；安全性信息的报告；临床试验数据记录以及病例报告表填写等。');
INSERT INTO `tbl_passage` VALUES (4690, 112, '第四十六条  为保证临床试验的质量，申办者可以组织独立于医疗器械临床试验、有相应培训和经验的稽查员对临床试验实施情况进行稽查，评估临床试验是否符合临床试验方案、本规范和相关法律法规的规定。');
INSERT INTO `tbl_passage` VALUES (4691, 112, '第四十七条  申办者应当确保医疗器械临床试验的实施遵守临床试验方案，发现医疗器械临床试验机构和研究者不遵守临床试验方案、本规范和相关法律法规的，应当及时指出并予以纠正；如情况严重或者持续不改，应当终止该临床试验机构和研究者继续参加该临床试验，并书面向临床试验机构所在地省、自治区、直辖市药品监督管理部门报告。');
INSERT INTO `tbl_passage` VALUES (4692, 112, '第四十八条  申办者应当在医疗器械临床试验暂停、终止或者完成后10个工作日内，书面报告所有的主要研究者、医疗器械临床试验机构管理部门、伦理委员会。申办者应当在医疗器械临床试验终止或者完成后10个工作日内，向申办者所在地省、自治区、直辖市药品监督管理部门报告。第六章  临床试验方案和试验报告');
INSERT INTO `tbl_passage` VALUES (4693, 112, '第四十九条  实施医疗器械临床试验，申办者应当根据试验目的，综合考虑试验医疗器械的风险、技术特征、适用范围和预期用途等，组织制定科学、合理的临床试验方案。');
INSERT INTO `tbl_passage` VALUES (4694, 112, '第五十条  临床试验方案一般包含产品基本信息、临床试验基本信息、试验目的、风险受益分析、试验设计要素、试验设计的合理性论证、统计学考虑、实施方式（方法、内容、步骤）、临床试验终点、数据管理、对临床试验方案修正的规定、不良事件和器械缺陷定义和报告的规定、伦理学考虑等内容。');
INSERT INTO `tbl_passage` VALUES (4695, 112, '第五十一条  申办者、主要研究者应当按照临床试验方案实施医疗器械临床试验，并完成临床试验报告。临床试验报告应当全面、完整、准确反映临床试验结果，临床试验报告安全性、有效性数据应当与临床试验源数据一致。');
INSERT INTO `tbl_passage` VALUES (4696, 112, '第五十二条  临床试验报告一般包含医疗器械临床试验基本信息、实施情况、统计分析方法、试验结果、不良事件和器械缺陷报告以及其处理情况、对试验结果的分析讨论、临床试验结论、伦理情况说明、存在问题以及改进建议等内容。');
INSERT INTO `tbl_passage` VALUES (4697, 112, '第五十三条  临床试验方案、临床试验报告应当由主要研究者签名、注明日期，经医疗器械临床试验机构审核签章后交申办者。第七章  多中心临床试验');
INSERT INTO `tbl_passage` VALUES (4698, 112, '第五十四条  多中心临床试验是指按照同一临床试验方案，在两个以上（含两个）医疗器械临床试验机构实施的临床试验。多中心临床试验在不同的国家或者地区实施时，为多区域临床试验，在中国境内实施的多区域医疗器械临床试验应当符合本规范的相关要求。');
INSERT INTO `tbl_passage` VALUES (4699, 112, '第五十五条  申办者实施多中心医疗器械临床试验，应当符合以下要求：');
INSERT INTO `tbl_passage` VALUES (4700, 112, '（一）申办者应当确保参加医疗器械临床试验的各中心均能遵守临床试验方案；');
INSERT INTO `tbl_passage` VALUES (4701, 112, '（二）申办者应当向各中心提供相同的临床试验方案。临床试验方案的伦理性和科学性经组长单位伦理委员会审查通过后,参加临床试验的其他医疗器械临床试验机构伦理委员会一般情况下不再对临床试验方案设计提出修改意见，但是有权不同意在其医疗器械临床试验机构进行试验；');
INSERT INTO `tbl_passage` VALUES (4702, 112, '（三）各中心应当使用相同的病例报告表和填写指导说明，以记录在医疗器械临床试验中获得的试验数据；');
INSERT INTO `tbl_passage` VALUES (4703, 112, '（四）医疗器械临床试验开始前，应当有书面文件明确参加医疗器械临床试验的各中心主要研究者的职责；');
INSERT INTO `tbl_passage` VALUES (4704, 112, '（五）申办者应当确保各中心主要研究者之间的沟通；');
INSERT INTO `tbl_passage` VALUES (4705, 112, '（六）申办者负责选择、确定医疗器械临床试验的协调研究者，协调研究者供职的医疗机构为组长单位。协调研究者承担多中心临床试验中各中心的协调工作。');
INSERT INTO `tbl_passage` VALUES (4706, 112, '第五十六条  多中心临床试验报告应当由协调研究者签名、注明日期，经组长单位医疗器械临床试验机构审核签章后交申办者。各分中心临床试验小结应当由该中心的主要研究者签名、注明日期，经该中心的医疗器械临床试验机构审核签章后交申办者。分中心临床试验小结主要包括人员信息、试验医疗器械和对照医疗器械（如适用）信息、试验概述、病例入组情况、临床试验方案的执行情况、试验数据的总结和描述性分析、医疗器械临床试验质量管理情况、不良事件和器械缺陷的发生以及处理情况、方案偏离情况说明等。第八章  记录要求');
INSERT INTO `tbl_passage` VALUES (4707, 112, '第五十七条  医疗器械临床试验数据应当真实、准确、完整、具有可追溯性。医疗器械临床试验的源数据应当清晰可辨识，不得随意更改；确需更改时应当说明理由，签名并注明日期。');
INSERT INTO `tbl_passage` VALUES (4708, 112, '第五十八条  在医疗器械临床试验中，主要研究者应当确保任何观察与发现均正确完整地予以记录。以患者为受试者的临床试验，相关的医疗记录应当载入门诊或者住院病历中。');
INSERT INTO `tbl_passage` VALUES (4709, 112, '第五十九条  主要研究者应当确保按照申办者提供的指南，填写和修改病例报告表，确保病例报告表中的数据准确、完整、清晰和及时。病例报告表中报告的数据应当与源文件一致。病例报告表中数据的修改，应当确保初始记录清晰可辨，保留修改轨迹，修改者签名并注明日期。');
INSERT INTO `tbl_passage` VALUES (4710, 112, '第六十条  医疗器械临床试验中如采用电子数据采集系统，该系统应当经过可靠的验证，具有完善的权限管理和稽查轨迹，可以追溯至记录的创建者、创建时间或者修改者、修改时间、修改情况，所采集的电子数据可以溯源。');
INSERT INTO `tbl_passage` VALUES (4711, 112, '第六十一条  医疗器械临床试验基本文件是用于评价申办者、医疗器械临床试验机构和主要研究者对本规范和药品监督管理部门有关要求的执行情况。药品监督管理部门可以对医疗器械临床试验基本文件进行检查，并作为确认医疗器械临床试验实施的真实性和所收集数据完整性的依据。');
INSERT INTO `tbl_passage` VALUES (4712, 112, '第六十二条  申办者和医疗器械临床试验机构应当具备临床试验基本文件保存的场所和条件，应当建立基本文件管理制度。医疗器械临床试验基本文件按临床试验阶段分为三部分：准备阶段文件、进行阶段文件、完成或者终止后文件。');
INSERT INTO `tbl_passage` VALUES (4713, 112, '第六十三条  申办者和医疗器械临床试验机构应当确保临床试验基本文件在保存期间的完整性，避免故意或者无意地更改或者丢失。');
INSERT INTO `tbl_passage` VALUES (4714, 112, '（一）研究者应当在医疗器械临床试验过程中妥善保存临床试验基本文件；');
INSERT INTO `tbl_passage` VALUES (4715, 112, '（二）医疗器械临床试验机构应当保存临床试验基本文件至医疗器械临床试验完成或者终止后10年；');
INSERT INTO `tbl_passage` VALUES (4716, 112, '（三）伦理委员会应当保存伦理审查的全部记录至医疗器械临床试验完成或者终止后10年；');
INSERT INTO `tbl_passage` VALUES (4717, 112, '（四）申办者应当保存临床试验基本文件至无该医疗器械使用时。第九章  附  则');
INSERT INTO `tbl_passage` VALUES (4718, 112, '第六十四条  本规范下列用语的含义： 医疗器械临床试验，是指在符合条件的医疗器械临床试验机构中，对拟申请注册的医疗器械（含体外诊断试剂）在正常使用条件下的安全性和有效性进行确认的过程。医疗器械临床试验机构，是指具备相应条件，按照本规范和相关法律法规实施医疗器械临床试验的机构，包括承担体外诊断试剂临床试验的血液中心和中心血站、设区的市级以上疾病预防控制机构、戒毒中心等非医疗机构。临床试验方案，是指说明医疗器械临床试验目的、设计、方法学和组织实施等的文件。临床试验方案包括方案以及其修订版。');
INSERT INTO `tbl_passage` VALUES (4719, 112, '临床试验报告，是指描述一项医疗器械临床试验设计、执行、统计分析和结果的文件。病例报告表，是指按照医疗器械临床试验方案所规定设计的文件，用以记录试验过程中获得的每个受试者的全部信息和数据。');
INSERT INTO `tbl_passage` VALUES (4720, 112, '研究者手册，是指申办者提供的，帮助主要研究者和参与临床试验的其他研究者更好地理解和遵守临床试验方案的资料汇编，包括但不限于：申办者基本信息、试验医疗器械的概要说明、支持试验医疗器械预期用途和临床试验设计理由的概要和评价、可能的风险、推荐的防范和紧急处理方法等。');
INSERT INTO `tbl_passage` VALUES (4721, 112, '试验医疗器械，是指医疗器械临床试验中对其安全性、有效性进行确认的拟申请注册的医疗器械。');
INSERT INTO `tbl_passage` VALUES (4722, 112, '对照医疗器械，是指医疗器械临床试验中作为对照的在中华人民共和国境内已上市医疗器械。伦理委员会，是指由适当人员组成的独立的委员会，其职责是确保参与医疗器械临床试验的受试者的权益和安全得到保护。');
INSERT INTO `tbl_passage` VALUES (4723, 112, '知情同意，是指向受试者告知医疗器械临床试验的各方面情况后，受试者确认自愿参加该项医疗器械临床试验的过程，应当以书面签署姓名和注明日期的知情同意书作为证明文件。');
INSERT INTO `tbl_passage` VALUES (4724, 112, '受试者，是指自愿参加医疗器械临床试验的个人。');
INSERT INTO `tbl_passage` VALUES (4725, 112, '公正见证人，是指与医疗器械临床试验无关，不受临床试验相关人员不公正影响的个人，在受试者无阅读能力时，作为公正的见证人，阅读知情同意书和其他提供给受试者的信息，并见证知情同意。');
INSERT INTO `tbl_passage` VALUES (4726, 112, '申办者，是指医疗器械临床试验的发起、管理和提供财务支持的机构或者组织。');
INSERT INTO `tbl_passage` VALUES (4727, 112, '研究者，是指在医疗器械临床试验机构中实施医疗器械临床试验的人员。');
INSERT INTO `tbl_passage` VALUES (4728, 112, '主要研究者，是指在医疗器械临床试验机构中实施医疗器械临床试验的负责人。');
INSERT INTO `tbl_passage` VALUES (4729, 112, '协调研究者，是指在多中心临床试验中由申办者指定实施协调工作的研究者，一般为组长单位的主要研究者。');
INSERT INTO `tbl_passage` VALUES (4730, 112, '监查，是指申办者为保证医疗器械临床试验能够遵守临床试验方案、本规范和相关法律法规，选派专门人员对医疗器械临床试验机构、研究者进行评价调查，对医疗器械临床试验过程中的数据进行验证并记录和报告的活动。');
INSERT INTO `tbl_passage` VALUES (4731, 112, '稽查，是指由申办者组织对医疗器械临床试验相关活动和文件进行系统性的独立检查，以确定此类活动的执行、数据的记录、分析和报告是否符合临床试验方案、本规范和相关法律法规。');
INSERT INTO `tbl_passage` VALUES (4732, 112, '检查，是指监管部门对医疗器械临床试验的有关文件、设施、记录和其他方面进行的监督管理活动。');
INSERT INTO `tbl_passage` VALUES (4733, 112, '偏离，是指有意或者无意地未遵守医疗器械临床试验方案要求的情形。');
INSERT INTO `tbl_passage` VALUES (4734, 112, '不良事件，是指在医疗器械临床试验过程中出现的不良医学事件，无论是否与试验医疗器械相关。');
INSERT INTO `tbl_passage` VALUES (4735, 112, '严重不良事件，是指医疗器械临床试验过程中发生的导致死亡或者健康状况严重恶化，包括致命的疾病或者伤害、身体结构或者身体功能的永久性缺陷、需要住院治疗或者延长住院时间、需要采取医疗措施以避免对身体结构或者身体功能造成永久性缺陷；导致胎儿窘迫、胎儿死亡或者先天性异常、先天缺损等事件。');
INSERT INTO `tbl_passage` VALUES (4736, 112, '器械缺陷，是指临床试验过程中医疗器械在正常使用情况下存在可能危及人体健康和生命安全的不合理风险，如标签错误、质量问题、故障等。');
INSERT INTO `tbl_passage` VALUES (4737, 112, '源数据，是指医疗器械临床试验中的临床发现、观察和其他活动的原始记录以及其经核准的副本中的所有信息，可以用于医疗器械临床试验重建和评价。');
INSERT INTO `tbl_passage` VALUES (4738, 112, '源文件，是指包含源数据的印刷文件、可视文件或者电子文件等。');
INSERT INTO `tbl_passage` VALUES (4739, 112, '');
INSERT INTO `tbl_passage` VALUES (4740, 112, '第六十五条  医疗器械临床试验方案等文书的格式范本由国家药品监督管理局另行制定。');
INSERT INTO `tbl_passage` VALUES (4741, 112, '第六十六条  本规范自2022年5月1日起施行。');
INSERT INTO `tbl_passage` VALUES (4742, 113, '附件医疗器械拓展性临床试验管理规定（试行）第一章  总  则');
INSERT INTO `tbl_passage` VALUES (4743, 113, '第一条  为满足公众临床需要，使临床急需治疗的患者能够尽快获得试验用医疗器械，规范医疗器械拓展性临床试验的开展和安全性数据的收集，维护受试者权益，根据中共中央办公厅、国务院办公厅《关于深化审评审批制度改革鼓励药品医疗器械创新的意见》，制定本规定。');
INSERT INTO `tbl_passage` VALUES (4744, 113, '第二条  在中华人民共和国境内开展医疗器械拓展性临床试验，应当遵守本规定。');
INSERT INTO `tbl_passage` VALUES (4745, 113, '第三条  本规定所称医疗器械拓展性临床试验，是指患有危及生命且尚无有效治疗手段的疾病的患者，可在开展临床试验的机构内使用尚未批准上市的医疗器械的活动和过程。拓展性临床试验医疗器械的使用需基于已有临床试验初步观察可能使患者获益，且患者由于临床试验机构已按临床试验方案完成病例的入选，不能通过参加已开展的临床试验获得该医疗器械的使用。');
INSERT INTO `tbl_passage` VALUES (4746, 113, '第四条  医疗器械拓展性临床试验应当符合医疗器械临床试验质量管理的相关规定。第二章  权益和责任');
INSERT INTO `tbl_passage` VALUES (4747, 113, '第五条  医疗器械拓展性临床试验应当遵循《世界医学大会赫尔辛基宣言》确定的伦理准则，保障受试者权益。在受试者参加拓展性临床试验前，受试者、研究者应当按照《医疗器械临床试验质量管理规范》的要求签署知情同意书。知情同意应当确保受试者了解其将接受试验用医疗器械的治疗，明确告知受试者拓展性临床试验所用医疗器械尚未获得境内上市许可，以及试验用医疗器械在安全性和有效性方面可能存在的风险。受试者参加医疗器械拓展性临床试验应当是自愿的，在试验过程中，受试者有权随时退出试验。伦理审查时应当充分保障受试者权益，基于医疗器械拓展性使用的潜在风险，考虑疾病的病程，确认受试者受益大于风险。');
INSERT INTO `tbl_passage` VALUES (4748, 113, '第六条  开展医疗器械拓展性临床试验的受试者、研究者、申办者和临床试验机构应当签订协议，明确各方权利、义务和责任。申办者、研究者、临床试验机构和伦理委员会应当参照《医疗器械临床试验质量管理规范》的要求承担各自的责任。医疗器械临床试验机构在建立医疗器械临床试验质量管理体系时，为有效控制医疗器械拓展性临床试验质量和风险，应当针对拓展性临床试验的特点增加相应管理制度和标准操作规范，在进行医疗器械临床试验机构备案时应当提交相应的自查报告，同时建立医疗器械拓展性临床试验公示制度，接受社会监督。公示至少包括如下内容：伦理委员会意见、拓展性临床试验开展的必要性、拓展性临床试验方案摘要。');
INSERT INTO `tbl_passage` VALUES (4749, 113, '第七条  开展医疗器械拓展性临床试验前，申办者应当向所在地省、自治区、直辖市药品监督管理部门备案，医疗器械临床试验机构应当向所在地的卫生健康行政部门报告。接受备案的药品监督管理部门应当将备案情况通报临床试验机构所在地的同级药品监督管理部门。省、自治区、直辖市药品监督管理部门和卫生健康行政部门应当依据各自职责加强对医疗器械拓展性临床试验的监督管理。');
INSERT INTO `tbl_passage` VALUES (4750, 113, '第八条  医疗器械拓展性临床试验，不影响相应医疗器械临床试验的进程。申办者应当积极尽责申请试验用医疗器械在中国境内的注册。');
INSERT INTO `tbl_passage` VALUES (4751, 113, '第九条  申办者、研究者及临床试验机构不得对拓展性临床试验用医疗器械收取费用。第三章  启动条件');
INSERT INTO `tbl_passage` VALUES (4752, 113, '第十条  拓展性临床试验用医疗器械的使用方法应当与正在开展的或者已结束的临床试验一致，其适用范围不应当超出正在开展的或者已结束的临床试验确认的适用范围。');
INSERT INTO `tbl_passage` VALUES (4753, 113, '第十一条  医疗器械拓展性临床试验应当在已开展该医疗器械临床试验的机构内进行，由患者、申办者或者研究者提出。');
INSERT INTO `tbl_passage` VALUES (4754, 113, '第十二条  研究者经判定认为患有危及生命且尚无有效治疗手段的疾病患者因使用试验用医疗器械引起的风险不大于疾病自身风险，预期的受益应当超过可能出现的损害。');
INSERT INTO `tbl_passage` VALUES (4755, 113, '第十三条  医疗器械临床试验机构应当具备与医疗器械拓展性临床试验相适应的组织管理和质量控制能力，同时具备以下条件：');
INSERT INTO `tbl_passage` VALUES (4756, 113, '（一）具有三级甲等医疗机构资质，已开展同种疾病研究和治疗，临床专业水平国内先进，具有与拟开展的拓展性临床试验相适应的诊疗项目；');
INSERT INTO `tbl_passage` VALUES (4757, 113, '（二）具有能够使用拓展性临床试验用医疗器械的医师，该医师具备相关使用经验或者培训经历；');
INSERT INTO `tbl_passage` VALUES (4758, 113, '（三）具备较高的医疗器械管理水平，已建立完善的医疗器械使用质量管理体系，具备医疗器械使用评价和医疗器械不良事件监测能力。');
INSERT INTO `tbl_passage` VALUES (4759, 113, '第十四条  医疗器械拓展性临床试验的主要研究者应当具有高级职称，参加过3个以上医疗器械临床试验。');
INSERT INTO `tbl_passage` VALUES (4760, 113, '第十五条  申办者应当会同研究者按照本章要求形成评估资料，供伦理审查使用。');
INSERT INTO `tbl_passage` VALUES (4761, 113, '第十六条  医疗器械拓展性临床试验的伦理审查应当有使用该医疗器械相应的专业医生参与。');
INSERT INTO `tbl_passage` VALUES (4762, 113, '第十七条  伦理委员会应当严格审议医疗器械拓展性临床试验受益与风险分析报告、临床试验方案及相关文件，确保该试验符合《医疗器械临床试验质量管理规范》和本规定的相应要求。第四章  过程管理');
INSERT INTO `tbl_passage` VALUES (4763, 113, '第十八条  申办者应当对医疗器械拓展性临床试验实施监查和核查，并会同研究者定期进行安全性和疗效评价，必要时可暂停或者终止拓展性临床试验。');
INSERT INTO `tbl_passage` VALUES (4764, 113, '第十九条  医疗器械拓展性临床试验过程中发现试验用医疗器械可能存在严重器械缺陷，研究者、申办者应当暂停或者终止拓展性临床试验，及时报告临床试验机构医疗器械临床试验管理部门。');
INSERT INTO `tbl_passage` VALUES (4765, 113, '第二十条  医疗器械拓展性临床试验过程中出现以下情形之一的，伦理委员会应当终止拓展性临床试验：');
INSERT INTO `tbl_passage` VALUES (4766, 113, '（一）受试者权益不能得到保障；');
INSERT INTO `tbl_passage` VALUES (4767, 113, '（二）出现与试验医疗器械可能有关的严重不良事件，或者出现临床试验结果未达到预期疗效，经伦理委员会评估认为拓展性临床试验风险大于受益情况；');
INSERT INTO `tbl_passage` VALUES (4768, 113, '（三）已开展的医疗器械临床试验方案有较大调整，但未对相应拓展性临床试验方案进行重新评估；');
INSERT INTO `tbl_passage` VALUES (4769, 113, '（四）最新研究显示医疗器械拓展性临床试验存在伦理性或者科学性问题。');
INSERT INTO `tbl_passage` VALUES (4770, 113, '第二十一条  医疗器械拓展性临床试验开展过程中出现以下情形之一的，临床试验自动终止。申办者、研究者、伦理委员会均应当及时启动终止程序，并报告临床试验机构医疗器械临床试验管理部门：');
INSERT INTO `tbl_passage` VALUES (4771, 113, '（一）试验用医疗器械或者与其相似的医疗器械已经在中国境内获准上市；');
INSERT INTO `tbl_passage` VALUES (4772, 113, '（二）与试验用医疗器械安全有效性相当的治疗方法已在临床得到使用；');
INSERT INTO `tbl_passage` VALUES (4773, 113, '（三）试验用医疗器械申请医疗器械注册未获得批准；');
INSERT INTO `tbl_passage` VALUES (4774, 113, '（四）存在违反本规定的情形。');
INSERT INTO `tbl_passage` VALUES (4775, 113, '第二十二条  被暂停的医疗器械拓展性临床试验，未经伦理委员会同意，不得恢复。第五章  数据收集');
INSERT INTO `tbl_passage` VALUES (4776, 113, '第二十三条  医疗器械拓展性临床试验数据的收集和提交应当合法、真实、完整、准确和可追溯。');
INSERT INTO `tbl_passage` VALUES (4777, 113, '第二十四条  医疗器械拓展性临床试验数据的分析评价主要关注安全性数据，在此基础上可兼顾有效性数据，以对拟注册产品进行全面评价。');
INSERT INTO `tbl_passage` VALUES (4778, 113, '第二十五条  医疗器械拓展性临床试验安全性数据的评价，主要关注使用试验用医疗器械时出现的不良事件及其发生率、持续时间、严重程度，以评价试验用医疗器械的预期风险。');
INSERT INTO `tbl_passage` VALUES (4779, 113, '第二十六条  申请医疗器械注册时，可以提交医疗器械拓展性临床试验方案、医疗器械拓展性临床试验数据，并对数据进行分析评价，形成分析评价报告。第六章  附  则');
INSERT INTO `tbl_passage` VALUES (4780, 113, '第二十七条  本规定由国家药品监督管理局和国家卫生健康委员会负责解释。');
INSERT INTO `tbl_passage` VALUES (4781, 113, '第二十八条  本规定自发布之日起施行。');
INSERT INTO `tbl_passage` VALUES (4782, 114, '国家食品药品监督管理总局 国家卫生和计划生育委员会关于发布医疗器械临床试验机构条件和备案管理办法的公告（2017年第145号） 发布时间：2017-11-24 　　根据《中共中央办公厅、国务院办公厅印发〈关于深化审评审批制度改革鼓励药品医疗器械创新的意见〉的通知》（厅字〔2017〕42号）和《国务院关于修改〈医疗器械监督管理条例〉的决定》（中华人民共和国国务院令第680号）规定，医疗器械临床试验机构由资质认定改为备案管理。国家食品药品监督管理总局会同国家卫生和计划生育委员会制定了《医疗器械临床试验机构条件和备案管理办法》，现予发布，自2018年1月1日起施行。\n食品药品监管总局 国家卫生计生委 \n2017年11月15日 医疗器械临床试验机构条件和备案管理办法第一章 总 则');
INSERT INTO `tbl_passage` VALUES (4783, 114, '第一条 为了加强和规范医疗器械临床试验机构管理，根据《医疗器械监督管理条例》的规定，制定本办法。');
INSERT INTO `tbl_passage` VALUES (4784, 114, '第二条 本办法所指的医疗器械临床试验机构备案，是指医疗器械临床试验机构按照本办法规定的条件和要求，将机构概况、专业技术水平、组织管理能力、伦理审查能力等信息提交食品药品监督管理部门进行存档、备查的过程。');
INSERT INTO `tbl_passage` VALUES (4785, 114, '第三条 本办法适用于在中华人民共和国境内开展医疗器械临床试验的机构备案管理工作。第二章 备案条件');
INSERT INTO `tbl_passage` VALUES (4786, 114, '第四条 医疗器械临床试验机构应当符合医疗器械临床试验质量管理规范的要求，具备开展医疗器械临床试验相应的专业技术水平、组织管理能力、伦理审查能力等以下条件：');
INSERT INTO `tbl_passage` VALUES (4787, 114, '（一）具有医疗机构执业资格；');
INSERT INTO `tbl_passage` VALUES (4788, 114, '（二）具有二级甲等以上资质；');
INSERT INTO `tbl_passage` VALUES (4789, 114, '（三）承担需进行临床试验审批的第三类医疗器械临床试验的，应为三级甲等医疗机构；');
INSERT INTO `tbl_passage` VALUES (4790, 114, '（四）具有医疗器械临床试验管理部门，配备适宜的管理人员、办公条件，并具有对医疗器械临床试验的组织管理和质量控制能力；');
INSERT INTO `tbl_passage` VALUES (4791, 114, '（五）具有符合医疗器械临床试验质量管理规范要求的伦理委员会；');
INSERT INTO `tbl_passage` VALUES (4792, 114, '（六）具有医疗器械临床试验管理制度和标准操作规程；');
INSERT INTO `tbl_passage` VALUES (4793, 114, '（七）具有与开展相关医疗器械临床试验相适应的诊疗科目，且应与医疗机构执业许可诊疗科目一致；');
INSERT INTO `tbl_passage` VALUES (4794, 114, '（八）具有能够承担医疗器械临床试验的人员，医疗器械临床试验主要研究者应当具有高级职称，其中开展创新医疗器械产品或需进行临床试验审批的第三类医疗器械产品临床试验的主要研究者应参加过3个以上医疗器械或药物临床试验；');
INSERT INTO `tbl_passage` VALUES (4795, 114, '（九）已开展相关医疗业务，能够满足医疗器械临床试验所需的受试人群要求等；');
INSERT INTO `tbl_passage` VALUES (4796, 114, '（十）具有防范和处理医疗器械临床试验中突发事件和严重不良事件的应急机制和处置能力；');
INSERT INTO `tbl_passage` VALUES (4797, 114, '（十一）国家食品药品监督管理总局、国家卫生和计划生育委员会规定的其他条件。');
INSERT INTO `tbl_passage` VALUES (4798, 114, '第五条 除符合本办法第四条条件的医疗机构外，其他承担体外诊断试剂临床试验的血液中心和中心血站、设区的市级以上疾病预防控制机构、戒毒中心等非医疗机构开展按医疗器械管理的体外诊断试剂临床试验，其应当具备以下条件：');
INSERT INTO `tbl_passage` VALUES (4799, 114, '（一）具有相应业务主管部门发放的机构资质证明文件；');
INSERT INTO `tbl_passage` VALUES (4800, 114, '（二）具有体外诊断试剂临床试验的管理部门，配备相应人员、办公条件，并具有对体外诊断试剂临床试验的组织管理和质量控制能力；');
INSERT INTO `tbl_passage` VALUES (4801, 114, '（三）能够开展伦理审查工作；');
INSERT INTO `tbl_passage` VALUES (4802, 114, '（四）具有体外诊断试剂临床试验管理制度和标准操作规程；');
INSERT INTO `tbl_passage` VALUES (4803, 114, '（五）具有与开展体外诊断试剂临床试验相适应的诊疗科目，且应与本机构业务范围一致；');
INSERT INTO `tbl_passage` VALUES (4804, 114, '（六）具有能够承担临床试验的人员，临床试验主要研究者应当具有高级职称；');
INSERT INTO `tbl_passage` VALUES (4805, 114, '（七）已开展相关业务，能够满足体外诊断试剂临床试验所需的受试人群要求等；');
INSERT INTO `tbl_passage` VALUES (4806, 114, '（八）具有防范和处理医疗器械临床试验中突发事件和严重不良事件的应急机制和处置能力；');
INSERT INTO `tbl_passage` VALUES (4807, 114, '（九）国家食品药品监督管理总局、国家卫生和计划生育委员会规定的其他条件。第三章 备案程序');
INSERT INTO `tbl_passage` VALUES (4808, 114, '第六条 国家食品药品监督管理总局组织建立医疗器械临床试验机构备案管理信息系统（以下简称备案系统），用于开展医疗器械临床试验机构备案管理工作。');
INSERT INTO `tbl_passage` VALUES (4809, 114, '第七条 医疗器械临床试验机构应当根据本办法的要求对本单位是否具备医疗器械临床试验条件和能力进行评估，并自行在备案系统中备案。');
INSERT INTO `tbl_passage` VALUES (4810, 114, '第八条 医疗器械临床试验机构应当按照要求，在备案系统中如实填写以下内容：');
INSERT INTO `tbl_passage` VALUES (4811, 114, '（一）机构名称、机构性质、地址、联系方式。');
INSERT INTO `tbl_passage` VALUES (4812, 114, '（二）机构级别、规模概况，包括床位、人员配备、建筑面积、医疗设备等。');
INSERT INTO `tbl_passage` VALUES (4813, 114, '（三）拟开展医疗器械临床试验的专业及主要研究者概况。');
INSERT INTO `tbl_passage` VALUES (4814, 114, '（四）医疗器械临床试验管理部门负责人和联系方式。');
INSERT INTO `tbl_passage` VALUES (4815, 114, '（五）提交包含如下内容的自查报告：');
INSERT INTO `tbl_passage` VALUES (4816, 114, '1.临床试验管理部门概况、人员介绍、管理制度、标准操作规程等；');
INSERT INTO `tbl_passage` VALUES (4817, 114, '2.伦理委员会或伦理审查工作概况，包括人员、制度等；');
INSERT INTO `tbl_passage` VALUES (4818, 114, '3.医疗器械临床试验质量管理体系建立运行概况；');
INSERT INTO `tbl_passage` VALUES (4819, 114, '4.临床试验管理部门人员、研究者的医疗器械临床试验相关法规和专业知识培训情况；');
INSERT INTO `tbl_passage` VALUES (4820, 114, '5.防范和处理医疗器械临床试验中突发事件、严重不良事件的应急机制和处置能力情况；');
INSERT INTO `tbl_passage` VALUES (4821, 114, '6.既往开展医疗器械临床试验的情况；');
INSERT INTO `tbl_passage` VALUES (4822, 114, '7.其他需要说明的情况。');
INSERT INTO `tbl_passage` VALUES (4823, 114, '第九条 医疗器械临床试验机构应按照备案系统要求，上传医疗机构执业资格许可证照、医疗机构级别证明文件、其他机构资质证明文件和资料符合性声明等材料。');
INSERT INTO `tbl_passage` VALUES (4824, 114, '第十条 医疗器械临床试验机构办理备案获得备案号后可以承担医疗器械临床试验。\n已备案的医疗器械临床试验机构名称、地址、联系人、联系方式和主要研究者等有关备案信息可在备案系统中查询。');
INSERT INTO `tbl_passage` VALUES (4825, 114, '第十一条 医疗器械临床试验机构名称、机构级别、机构负责人员、地址、伦理委员会、医疗器械临床试验专业和主要研究者备案信息发生变化时，医疗器械临床试验机构应当登录备案系统，在线填写相关信息变更情况。');
INSERT INTO `tbl_passage` VALUES (4826, 114, '第十二条 医疗器械临床试验机构应当在每年1月31日前在线提交上一年度开展医疗器械临床试验工作总结报告。');
INSERT INTO `tbl_passage` VALUES (4827, 114, '第十三条 医疗器械临床试验机构决定不再开展医疗器械临床试验的，应登录备案系统，取消备案。第四章 监督管理');
INSERT INTO `tbl_passage` VALUES (4828, 114, '第十四条 省级以上食品药品监督管理部门应当每年定期将本行政区域医疗器械临床试验机构备案的相关信息通报同级卫生计生行政部门。');
INSERT INTO `tbl_passage` VALUES (4829, 114, '第十五条 省级食品药品监督管理部门、卫生计生行政部门应按照各自监管职责，加强对本行政区域医疗器械临床试验机构的监督管理和信息通报。对发现的违法违规行为，按照《医疗器械监督管理条例》及其他相关法规规定组织查处。');
INSERT INTO `tbl_passage` VALUES (4830, 114, '第十六条 隐瞒有关情况或者提供虚假材料办理临床试验机构备案的，或者存在缺陷、不适宜继续承担临床试验的临床试验机构，省级以上食品药品监督管理部门按照《医疗器械监督管理条例》的规定进行处理。国家食品药品监督管理总局取消其机构或相关专业的备案信息，通报国家卫生和计划生育委员会，并进行公告。');
INSERT INTO `tbl_passage` VALUES (4831, 114, '第十七条 医疗器械临床试验机构的备案信息涉及国家机密、商业秘密或者个人隐私的，应当符合《中华人民共和国保守国家秘密法》及其他相关法律法规的规定。第五章 附 则');
INSERT INTO `tbl_passage` VALUES (4832, 114, '第十八条 医疗器械临床试验机构备案号格式为：械临机构备+4位年代号+5位顺序编号。');
INSERT INTO `tbl_passage` VALUES (4833, 114, '第十九条 食品药品监督管理部门实施临床试验机构备案和监督管理，不得收取任何费用。');
INSERT INTO `tbl_passage` VALUES (4834, 114, '第二十条 本办法自2018年1月1日起施行。');
INSERT INTO `tbl_passage` VALUES (4835, 115, '附件无源植入性医疗器械临床试验审批申报资料编写指导原则为进一步规范无源植入性医疗器械产品临床试验审批申报资料要求，指导申请人对需进行临床试验审批的无源植入性医疗器械的申报资料进行准备，制订本指导原则。本指导原则虽然为该类产品的临床试验审批申报资料的准备提供了初步指导和建议，但不会限制医疗器械相关管理部门对该类产品的技术审评、行政审批，以及申请人对该类产品临床试验审批申报资料的准备工作。本指导原则是在现行法规以及当前认知水平下制订的，随着医疗器械临床试验和管理法规的不断完善，本指导原则相关内容也将进行适时地调整。');
INSERT INTO `tbl_passage` VALUES (4836, 115, '一、适用范围本指导原则适用于需进行临床试验审批的第三类无源植入性医疗器械。');
INSERT INTO `tbl_passage` VALUES (4837, 115, '二、基本原则申请人应按照相关要求提供申报资料，在此基础上，本指导原则对《关于公布医疗器械注册申报资料要求和批准证明文件格式的公告》（国家食品药品监督管理总局公告2014年第43号）中的试验产品描述、临床前研究资料、临床试验方案部分给予指导。');
INSERT INTO `tbl_passage` VALUES (4838, 115, '三、试验产品描述应当包括试验用医疗器械的设计原理、工作原理、产品特征、结构组成及图示、制造材料、包装材料、型号规格及其划分依据、主要生产工艺、交付状态、作用机理、适用范围等内容。');
INSERT INTO `tbl_passage` VALUES (4839, 115, '（一）设计原理及工作原理');
INSERT INTO `tbl_passage` VALUES (4840, 115, '1. 通过医学理论、病变特征、同现有器械和/或治疗方法在临床使用风险和受益方面的对比等阐述产品研发背景；');
INSERT INTO `tbl_passage` VALUES (4841, 115, '2. 阐述产品的设计原理及工作原理并同时提供产品结构示意图、使用方法、操作图示等。');
INSERT INTO `tbl_passage` VALUES (4842, 115, '（二）产品特征对产品特征进行描述，重点描述区别于其他同类产品的产品特征；提供区别于其他同类产品的产品特征的设计依据。');
INSERT INTO `tbl_passage` VALUES (4843, 115, '（三）结构组成及图示');
INSERT INTO `tbl_passage` VALUES (4844, 115, '1. 明确说明产品结构组成，包含配件/附件；如产品的结构组成为系统，应分别进行描述。');
INSERT INTO `tbl_passage` VALUES (4845, 115, '2. 提供产品的结构图、实物图及整体剖面结构图，关键位置应提供局部工程结构图及剖面结构图；结构图中应标明部件名称及重要尺寸信息，测量位置（如适用）应明确。');
INSERT INTO `tbl_passage` VALUES (4846, 115, '（四）制造材料');
INSERT INTO `tbl_passage` VALUES (4847, 115, '1. 列表明确各组成部分的制造材料。列表中应同时明确配方（如适用）并注明各成分的化学名称、化学结构式或分子式、牌号（如有）、供应商、商品名（如有）、预期与人体接触时间和方式等。提供材料列表时，应注意以下方面：');
INSERT INTO `tbl_passage` VALUES (4848, 115, '（1）材料为混合物的应明确比例。');
INSERT INTO `tbl_passage` VALUES (4849, 115, '（2）组成部分为分层分段结构的应分别描述制造材料；');
INSERT INTO `tbl_passage` VALUES (4850, 115, '（3）应与产品结构图示有明确的对应关系；');
INSERT INTO `tbl_passage` VALUES (4851, 115, '（4）明确材料符合的标准（如适用）。');
INSERT INTO `tbl_passage` VALUES (4852, 115, '2. 概述制造材料与安全性相关质控措施，如入厂质控标准及检验报告总结。');
INSERT INTO `tbl_passage` VALUES (4853, 115, '3. 特殊材料如可生物吸收材料应提供材料选择依据。');
INSERT INTO `tbl_passage` VALUES (4854, 115, '4. 若产品供货状态是在液体中，则提供液体的详细成分及含量信息，以及相关安全性评价资料总结。');
INSERT INTO `tbl_passage` VALUES (4855, 115, '（五）包装材料');
INSERT INTO `tbl_passage` VALUES (4856, 115, '1. 列表明确产品的初包装材料；');
INSERT INTO `tbl_passage` VALUES (4857, 115, '2. 提供初包装图示，标明包装尺寸、容积（如适用）信息；');
INSERT INTO `tbl_passage` VALUES (4858, 115, '3. 明确与产品一起销售的配件/附件包装情况；');
INSERT INTO `tbl_passage` VALUES (4859, 115, '4. 初包装材料的安全性评价资料（如适用，例如液体类产品），如初包装材料与产品的相容性。');
INSERT INTO `tbl_passage` VALUES (4860, 115, '（六）型号规格及其划分依据');
INSERT INTO `tbl_passage` VALUES (4861, 115, '1. 型号、规格应能区分和识别同一家企业生产但具有不同功能范围、技术特征的产品。');
INSERT INTO `tbl_passage` VALUES (4862, 115, '2. 如含有多种型号产品时，应属于同一注册单元。');
INSERT INTO `tbl_passage` VALUES (4863, 115, '3. 如分别规定了型号和规格，应首先明确型号间的所有区别，再明确同一型号下不同规格产品间的所有区别；必要时附相应图示进行说明。');
INSERT INTO `tbl_passage` VALUES (4864, 115, '4. 采用对比表及带有说明性文字的图片、图表，对于各种型号规格的结构组成、功能、产品特征、性能指标等方面加以描述。');
INSERT INTO `tbl_passage` VALUES (4865, 115, '（七）主要生产工艺');
INSERT INTO `tbl_passage` VALUES (4866, 115, '1. 概述产品生产加工工艺，注明关键工艺和特殊工艺，并说明其过程控制点；');
INSERT INTO `tbl_passage` VALUES (4867, 115, '2. 概述生产过程中各种加工助剂（如溶剂、催化剂）的使用情况及对杂质（如残留单体、小分子残留物等）的控制情况。');
INSERT INTO `tbl_passage` VALUES (4868, 115, '（八）交付状态明确产品是否以无菌状态提供，明确灭菌方式及货架有效期。说明产品是一次性使用还是重复使用。');
INSERT INTO `tbl_passage` VALUES (4869, 115, '（九）适用范围明确适用人群、适应证、禁忌症，适用范围不应与拟开展的临床试验入/排标准矛盾。');
INSERT INTO `tbl_passage` VALUES (4870, 115, '四、临床前研究资料对于首次用于植入性医疗器械方面的新材料，应提供该材料适合用于人体使用的相关研究资料。');
INSERT INTO `tbl_passage` VALUES (4871, 115, '（一）实验室研究总结研究总结应至少包含以下要素：');
INSERT INTO `tbl_passage` VALUES (4872, 115, '1. 实验目的和背景信息；');
INSERT INTO `tbl_passage` VALUES (4873, 115, '2. 验证项目及其接受标准、实验方法以及三者的确定依据，如为企业自建的实验方法，应同时提供方法学研究总结；');
INSERT INTO `tbl_passage` VALUES (4874, 115, '3. 实验用规格、型号选择的依据；');
INSERT INTO `tbl_passage` VALUES (4875, 115, '4. 实验样本数量的确定依据，适用项目宜结合统计学考虑；');
INSERT INTO `tbl_passage` VALUES (4876, 115, '5. 结果分析，适用时进行统计学分析；方案偏离分析；');
INSERT INTO `tbl_passage` VALUES (4877, 115, '6. 结论。');
INSERT INTO `tbl_passage` VALUES (4878, 115, '（二）生物相容性评价研究资料参照相关法规、规章及GB/T16886系列标准，提供完备的生物相容性评价报告；未采用GB/T16886系列标准时应提供依据。');
INSERT INTO `tbl_passage` VALUES (4879, 115, '（三）生物安全性研究资料');
INSERT INTO `tbl_passage` VALUES (4880, 115, '1. 含有同种异体材料、动物源性材料或生物活性物质等具有生物安全风险类产品，概述组织、细胞和材料的获取、加工、保存、测试和处理过程，阐述来源（包括捐献者筛选细节），应符合相关标准和规定（如有），并提供相应支持性资料；');
INSERT INTO `tbl_passage` VALUES (4881, 115, '2. 描述生产过程中对病毒、其他病原体去除或灭活方法，提供病毒灭活验证报告；');
INSERT INTO `tbl_passage` VALUES (4882, 115, '3. 描述生产过程中对免疫原性物质去除方法，提供对免疫原性物质去除方法的验证报告（如适用）；');
INSERT INTO `tbl_passage` VALUES (4883, 115, '4. 可参照《动物源性医疗器械注册技术审查指导原则（2017年修订版）》（国家食品药品监督管理总局通告2017年第224号）、《同种异体植入性医疗器械病毒灭活工艺验证指导原则》（食药监办械函〔2011〕116号）提供相关资料。');
INSERT INTO `tbl_passage` VALUES (4884, 115, '（四）灭菌/消毒工艺研究总结');
INSERT INTO `tbl_passage` VALUES (4885, 115, '（五）产品有效期和包装研究总结研究总结中至少包含以下要素：');
INSERT INTO `tbl_passage` VALUES (4886, 115, '1. 货架有效期；');
INSERT INTO `tbl_passage` VALUES (4887, 115, '2. 有效期研究方法，如为加速老化实验应明确条件并确认实验条件的适宜性；');
INSERT INTO `tbl_passage` VALUES (4888, 115, '3. 产品稳定性研究和包装完整性研究总结；');
INSERT INTO `tbl_passage` VALUES (4889, 115, '4. 运输及储运条件。');
INSERT INTO `tbl_passage` VALUES (4890, 115, '（六）动物试验研究总结（如适用）研究总结应至少包含以下要素：');
INSERT INTO `tbl_passage` VALUES (4891, 115, '1. 实验目的和背景信息；');
INSERT INTO `tbl_passage` VALUES (4892, 115, '2. 动物种类、模型及其确定依据；');
INSERT INTO `tbl_passage` VALUES (4893, 115, '3. 动物数量及其确定依据；');
INSERT INTO `tbl_passage` VALUES (4894, 115, '4. 实验用器械样品选择依据；');
INSERT INTO `tbl_passage` VALUES (4895, 115, '5. 是否采用对照研究，对照用医疗器械的确定依据；');
INSERT INTO `tbl_passage` VALUES (4896, 115, '6. 研究指标的确定依据；');
INSERT INTO `tbl_passage` VALUES (4897, 115, '7. 观察时间点的确定依据；');
INSERT INTO `tbl_passage` VALUES (4898, 115, '8. 实验方法描述；');
INSERT INTO `tbl_passage` VALUES (4899, 115, '9. 研究指标结果分析，适用时进行统计学分析，方案偏离分析；');
INSERT INTO `tbl_passage` VALUES (4900, 115, '10. 实验结论。为保证动物实验的规范性，建议在符合相关规定和标准要求的实验室开展动物实验。');
INSERT INTO `tbl_passage` VALUES (4901, 115, '五、临床试验方案');
INSERT INTO `tbl_passage` VALUES (4902, 115, '（一）临床试验方案应当符合《医疗器械临床试验质量管理规范》相关要求，并提交证明临床试验方案科学合理性的分析资料如主要研究终点、对照组选择、样本量、随访时间等的确定依据。');
INSERT INTO `tbl_passage` VALUES (4903, 115, '（二）如国内有适用的指导原则，建议参照指导原则进行临床试验方案设计；如无，可参照《无源植入性医疗器械产品注册申报资料指导原则》（食药监办械函〔2009〕519号）第三章第四部分要求。');
INSERT INTO `tbl_passage` VALUES (4904, 115, '六、参考文献');
INSERT INTO `tbl_passage` VALUES (4905, 115, '1.《》（中华人民共和国国务院令第680号）');
INSERT INTO `tbl_passage` VALUES (4906, 115, '2.《》（国家食品药品监督管理总局令第4号）');
INSERT INTO `tbl_passage` VALUES (4907, 115, '3.《医疗器械临床试验质量管理规范》（国家食品药品监督管理总局、中华人民共和国国家卫生和计划生育委员会令第25号）');
