INSERT INTO `tbl_passage` VALUES (5114, 132, '（1）科学数据库–如中国期刊全文数据库、美国《医学索引》（MEDLINE）、荷兰《医学文摘》（EMBASE）');
INSERT INTO `tbl_passage` VALUES (5115, 132, '（2）系统综述数据库（如科克伦系统评价数据库（Cochrane Database of Systematic Reviews））');
INSERT INTO `tbl_passage` VALUES (5116, 132, '（3）临床试验注册中心（如科克伦临床对照试验中心注册数据库（CENTRAL））包括来源数据库的选择理由，说明提高检出率的辅助策略 （如检查文献的参考书目、人工检索文献等）');
INSERT INTO `tbl_passage` VALUES (5117, 132, '5.检索详细信息');
INSERT INTO `tbl_passage` VALUES (5118, 132, '（1）检索词（关键词、索引词）及其关系');
INSERT INTO `tbl_passage` VALUES (5119, 132, '（2）所用媒体，如线上、CD-ROM（包括发布日期和版本）');
