INSERT INTO `tbl_passage` VALUES (7265, 174, '一、监管信息');
INSERT INTO `tbl_passage` VALUES (7266, 174, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7267, 174, '（二）申请表按照填表要求填写。');
INSERT INTO `tbl_passage` VALUES (7268, 174, '（三）关联文件');
INSERT INTO `tbl_passage` VALUES (7269, 174, '1.境内注册人应当提交企业营业执照副本或事业单位法人证书复印件；境外注册人应当提交企业资格证明文件。');
INSERT INTO `tbl_passage` VALUES (7270, 174, '2.境外注册人应当提交在中国境内指定代理人的委托书、代理人承诺书及营业执照副本复印件。');
INSERT INTO `tbl_passage` VALUES (7271, 174, '3.提交原医疗器械注册证及其附件的复印件、历次医疗器械变更注册（备案）文件及其附件的复印件。');
INSERT INTO `tbl_passage` VALUES (7272, 174, '4.如医疗器械注册证有效期内有新的医疗器械强制性标准发布实施，已注册产品为符合新的强制性标准所做的变化属于应当办理变更注册的，注册人应当提交申请延续注册前已获得原审批部门批准的变更注册（备案）文件及其附件的复印件。已注册产品为符合新的强制性标准所做的变化属于无需办理变更注册手续或者无需变化即可符合新的强制性标准的，注册人应当提供情况说明和相关证明资料。进口医疗器械延续注册时，不需要提供注册人注册地或者生产地所在国家（地区）批准产品上市销售的证明文件。');
INSERT INTO `tbl_passage` VALUES (7273, 174, '（四）申报前与监管机构的联系情况和沟通记录');
INSERT INTO `tbl_passage` VALUES (7274, 174, '1.在注册证有效期内，如果注册人与监管机构针对申报产品以会议形式进行了沟通，应当提供下列内容（如适用）：');
INSERT INTO `tbl_passage` VALUES (7275, 174, '（1）列出监管机构回复的沟通情况。');
INSERT INTO `tbl_passage` VALUES (7276, 174, '（2）在沟通中，注册人明确提出的问题，以及监管机构提供的建议。');
INSERT INTO `tbl_passage` VALUES (7277, 174, '（3）说明在本次申报中如何解决上述问题。');
INSERT INTO `tbl_passage` VALUES (7278, 174, '2.如不适用，应当明确声明申报产品在注册证有效期内没有既往申报和/或申报前沟通。');
INSERT INTO `tbl_passage` VALUES (7279, 174, '（五）符合性声明注册人应当声明下列内容：');
INSERT INTO `tbl_passage` VALUES (7280, 174, '1.延续注册产品没有变化。如产品发生了注册证载明事项以外变化的，应当明确“产品所发生的变化通过质量管理体系进行控制，注册证载明事项无变化”。');
INSERT INTO `tbl_passage` VALUES (7281, 174, '2.延续注册产品符合《医疗器械注册与备案管理办法》和相关法规的要求。');
INSERT INTO `tbl_passage` VALUES (7282, 174, '3.延续注册产品符合《医疗器械分类规则》有关分类的要求。');
INSERT INTO `tbl_passage` VALUES (7283, 174, '4.延续注册产品符合现行国家标准、行业标准，并提供符合标准的清单。');
INSERT INTO `tbl_passage` VALUES (7284, 174, '5.保证所提交资料的真实性（境内产品由注册人出具，进口产品由注册人和代理人分别出具）。');
INSERT INTO `tbl_passage` VALUES (7285, 174, '二、非临床资料');
INSERT INTO `tbl_passage` VALUES (7286, 174, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7287, 174, '（二）产品技术要求如在原医疗器械注册证有效期内发生了涉及产品技术要求变更的，应当提交依据变更注册（备案）文件修改的产品技术要求。');
INSERT INTO `tbl_passage` VALUES (7288, 174, '（三）其他资料原医疗器械注册证中载明要求继续完成的事项，涉及非临床研究的，应当提供相关总结报告，并附相应资料。');
INSERT INTO `tbl_passage` VALUES (7289, 174, '三、临床评价资料原医疗器械注册证中载明要求继续完成的事项，涉及临床评价的，应当提供相关总结报告，并附相应资料。');
INSERT INTO `tbl_passage` VALUES (7290, 175, '附件4医疗器械注册申报资料和批准证明文件格式要求');
INSERT INTO `tbl_passage` VALUES (7291, 175, '一、申报资料格式要求按照医疗器械电子申报系统申报的，注册申报资料应当符合电子申报的格式要求。未按照医疗器械电子申报系统申报的，注册申报资料应当符合以下格式要求，除特别说明适用于境内产品或进口产品申报资料的内容，其余内容对所有类型申报产品均适用。');
INSERT INTO `tbl_passage` VALUES (7292, 175, '（一）形式要求');
INSERT INTO `tbl_passage` VALUES (7293, 175, '1.申报资料应当有所提交资料目录，包括申报资料的一级和二级标题。每项二级标题对应的资料应当单独编制页码。');
INSERT INTO `tbl_passage` VALUES (7294, 175, '2.申报资料应当按目录顺序排列并装订成册。');
INSERT INTO `tbl_passage` VALUES (7295, 175, '3.申报资料一式一份，其中产品技术要求一式两份，应当使用A4规格纸张打印，内容完整、清楚，不得涂改，政府部门及其他机构出具的文件按照原件尺寸提供。凡装订成册的，不得自行拆分。');
INSERT INTO `tbl_passage` VALUES (7296, 175, '4.申报资料使用复印件的，复印件应当清晰并与原件一致。');
INSERT INTO `tbl_passage` VALUES (7297, 175, '5.各项申报资料中的申请内容应当具有一致性。');
INSERT INTO `tbl_passage` VALUES (7298, 175, '6.各项文件除关联文件外，均应当以中文形式提供，如证明性文件为外文形式，还应当提供中文译本并由代理人签章。根据外文资料翻译的申报资料，应当同时提供原文。');
INSERT INTO `tbl_passage` VALUES (7299, 175, '（二）签章和公证要求');
INSERT INTO `tbl_passage` VALUES (7300, 175, '1.境内产品申报资料如无特殊说明的，应当由注册申请人签章。“签章”是指：注册申请人盖公章，或者其法定代表人、负责人签名并加盖公章。');
INSERT INTO `tbl_passage` VALUES (7301, 175, '2.进口产品申报资料如无特别说明，原文资料均应当由注册申请人签章，中文资料由代理人签章。原文资料“签章”是指：注册申请人的法定代表人或者负责人签名，或者签名并加盖组织机构印章；中文资料“签章”是指：代理人盖公章，或者其法定代表人、负责人签名并加盖公章。');
INSERT INTO `tbl_passage` VALUES (7302, 175, '3.进口产品申报资料中由境外注册申请人提供的关联文件、符合性声明以及说明书、标签应当提交由注册申请人所在地公证机构出具的公证件。公证件可以是通过电子公证模式办理的，但应当同时提交由境外注册申请人出具的关于新公证模式的说明文件。');
INSERT INTO `tbl_passage` VALUES (7303, 175, '（三）电子文档要求下列注册申报资料还需同时提交电子文档：');
INSERT INTO `tbl_passage` VALUES (7304, 175, '1.申请表。');
INSERT INTO `tbl_passage` VALUES (7305, 175, '2.产品技术要求。应当为word文档，并且可编辑、修改。');
INSERT INTO `tbl_passage` VALUES (7306, 175, '3.综述资料、非临床研究综述以及产品说明书。应当为word文档，并且可编辑、修改。');
INSERT INTO `tbl_passage` VALUES (7307, 175, '4.临床试验数据库根据文件类型，采用适宜的电子文档，包括pdf、word、excel、xpt、txt等，word、excel、txt等文件应当可编辑、修改。');
INSERT INTO `tbl_passage` VALUES (7308, 175, '二、批准证明文件格式要求');
INSERT INTO `tbl_passage` VALUES (7309, 175, '（一）批准证明文件制作的原则要求制证人员应当按照行政审批结论制作批件。');
INSERT INTO `tbl_passage` VALUES (7310, 175, '1.制作的《医疗器械注册证》、《医疗器械变更注册（备案）文件》内容完整、准确无误，加盖的医疗器械注册专用章准确、无误。');
INSERT INTO `tbl_passage` VALUES (7311, 175, '2.制作的《不予行政许可决定书》中须写明不予行政许可的理由，并注明申请人依法享有申请行政复议或者提起行政诉讼的权利以及投诉渠道。');
INSERT INTO `tbl_passage` VALUES (7312, 175, '3.其他许可文书等应当符合公文的相关要求。');
INSERT INTO `tbl_passage` VALUES (7313, 175, '（二）批准证明文件制作的具体要求');
INSERT INTO `tbl_passage` VALUES (7314, 175, '1.《医疗器械注册证》《医疗器械注册证》栏内填写内容较多的，可采用附页形式。不适用的栏目，应当标注“不适用”。《医疗器械注册证》及附件所列内容为注册限定内容。如药品监督管理部门经注册审查，认为《医疗器械注册证》中除已明确规定需载明的内容外仍有其他内容需要载明，应当在《医疗器械注册证》“其他内容”栏目中列出，内容较多可采用附件形式。进口产品《医疗器械注册证》中产品名称、注册人名称应当使用中文，可附加英文或原文，注册人住所和生产地址可使用中文、英文或原文。');
INSERT INTO `tbl_passage` VALUES (7315, 175, '2.《医疗器械变更注册（备案）文件》《医疗器械变更注册（备案）文件》中“变更内容”栏的填写：变更内容在国家药品监督管理局/省级药品监督管理部门政府网站上予以公布的，填写变更后内容，例如“注册人名称变更为×××”、“代理人住所变更为×××”；变更内容不在国家药品监督管理局/省级药品监督管理部门政府网站上予以公布的，填写变更项目，例如“产品技术要求中检验方法变更”。');
INSERT INTO `tbl_passage` VALUES (7316, 175, '3.补发《医疗器械注册证》或《医疗器械变更注册（备案）文件》在备注栏加注“××××年××月××日补发”，其他内容不变。');
INSERT INTO `tbl_passage` VALUES (7317, 175, '4.《医疗器械注册证》和《医疗器械变更注册（备案）文件》等用A4纸打印。');
INSERT INTO `tbl_passage` VALUES (7318, 175, '5.《医疗器械注册证》和《医疗器械变更注册（备案）文件》等可采用电子文件。');
INSERT INTO `tbl_passage` VALUES (7319, 175, '（三）批准文件附件发放要求国家药品监督管理局/省级药品监督管理部门应当将经审查核准的产品技术要求进行编号并加盖医疗器械注册专用章，作为注册证附件发给申请人。产品技术要求的标题为“×××（产品名称）产品技术要求”，编号即为相应的注册证编号。变更产品技术要求的，国家药品监督管理局/省级药品监督管理部门应当将经审查核准的产品技术要求变更对比表，加盖医疗器械注册专用章，随变更注册（备案）文件一并发给注册人。');
INSERT INTO `tbl_passage` VALUES (7320, 176, '附件5医疗器械注册申报资料要求及说明');
INSERT INTO `tbl_passage` VALUES (7321, 176, '一、监管信息');
INSERT INTO `tbl_passage` VALUES (7322, 176, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7323, 176, '（二）申请表按照填表要求填写。');
INSERT INTO `tbl_passage` VALUES (7324, 176, '（三）术语、缩写词列表如适用，应当根据注册申报资料的实际情况，对其中出现的需要明确含义的术语或缩写词进行定义。');
INSERT INTO `tbl_passage` VALUES (7325, 176, '（四）产品列表以表格形式列出拟申报产品的型号、规格、结构及组成、附件，以及每个型号规格的标识（如型号或部件的编号，器械唯一标识等）和描述说明（如尺寸、材质等）。');
INSERT INTO `tbl_passage` VALUES (7326, 176, '（五）关联文件');
INSERT INTO `tbl_passage` VALUES (7327, 176, '1.境内申请人应当提供：');
INSERT INTO `tbl_passage` VALUES (7328, 176, '（1）企业营业执照副本或事业单位法人证书的复印件。');
INSERT INTO `tbl_passage` VALUES (7329, 176, '（2）按照《创新医疗器械特别审查程序》审批的境内医疗器械申请注册时，应当提交通过创新医疗器械审查的相关说明。');
INSERT INTO `tbl_passage` VALUES (7330, 176, '（3）按照《医疗器械应急审批程序》审批的医疗器械产品申请注册时，应当提交通过医疗器械应急审批的相关说明。');
INSERT INTO `tbl_passage` VALUES (7331, 176, '（4）委托其他企业生产的，应当提供受托企业资格文件（营业执照副本复印件）、委托合同和质量协议。');
INSERT INTO `tbl_passage` VALUES (7332, 176, '（5）进口医疗器械注册人通过其在境内设立的外商投资企业按照进口医疗器械产品在中国境内企业生产有关规定申请注册时，应当提交进口医疗器械注册人同意注册申报的声明或授权文件；还应提供申请人与进口医疗器械注册人的关系（包括法律责任）说明文件，应当附相关协议、质量责任、股权证明等文件。');
INSERT INTO `tbl_passage` VALUES (7333, 176, '2.境外申请人应当提供：');
INSERT INTO `tbl_passage` VALUES (7334, 176, '（1）企业资格证明文件：境外申请人注册地所在国家（地区）公司登记主管部门或医疗器械主管部门出具的能够证明境外申请人存续且具备相应医疗器械生产资格的证明文件；或第三方认证机构为境外申请人出具的能够证明境外申请人具备相应医疗器械生产资格的证明文件。');
INSERT INTO `tbl_passage` VALUES (7335, 176, '（2）境外申请人注册地或生产地所在国家（地区）医疗器械主管部门出具的准许该产品上市销售的证明文件，未在境外申请人注册地或生产地所在国家（地区）上市的创新医疗器械可以不提交。');
INSERT INTO `tbl_passage` VALUES (7336, 176, '（3）境外申请人注册地或者生产地所在国家（地区）未将该产品作为医疗器械管理的，申请人需要提供相关文件，包括注册地或者生产地所在国家（地区）准许该产品上市销售的证明文件，未在境外申请人注册地或生产地所在国家（地区）上市的创新医疗器械可以不提交。');
INSERT INTO `tbl_passage` VALUES (7337, 176, '（4）在中国境内指定代理人的委托书、代理人承诺书及营业执照副本复印件。');
INSERT INTO `tbl_passage` VALUES (7338, 176, '（5）按照《创新医疗器械特别审查程序》审批的进口医疗器械申请注册时，应当提交通过创新医疗器械审查的相关说明。');
INSERT INTO `tbl_passage` VALUES (7339, 176, '（6）按照《医疗器械应急审批程序》审批的进口医疗器械产品申请注册时，应当提交通过医疗器械应急审批的相关说明。');
INSERT INTO `tbl_passage` VALUES (7340, 176, '（7）委托其他企业生产的，应当提供受托企业资格文件、委托合同和质量协议。');
INSERT INTO `tbl_passage` VALUES (7341, 176, '3.主文档授权信如适用，申请人应当对主文档引用的情况进行说明。申请人应当提交由主文档所有者或其备案代理机构出具的授权申请人引用主文档信息的授权信。授权信中应当包括引用主文档的申请人信息、产品名称、已备案的主文档编号、授权引用的主文档页码/章节信息等内容。');
INSERT INTO `tbl_passage` VALUES (7342, 176, '（六）申报前与监管机构的联系情况和沟通记录');
INSERT INTO `tbl_passage` VALUES (7343, 176, '1.在产品申报前，如果申请人与监管机构针对申报产品以会议形式进行了沟通，或者申报产品与既往注册申报相关。应当提供下列内容（如适用）：');
INSERT INTO `tbl_passage` VALUES (7344, 176, '（1）列出监管机构回复的申报前沟通。');
INSERT INTO `tbl_passage` VALUES (7345, 176, '（2）既往注册申报产品的受理号。');
INSERT INTO `tbl_passage` VALUES (7346, 176, '（3）既往申报前沟通的相关资料，如既往申报会议前提交的信息、会议议程、演示幻灯片、最终的会议纪要、会议中待办事项的回复，以及所有与申请相关的电子邮件。');
INSERT INTO `tbl_passage` VALUES (7347, 176, '（4）既往申报（如自行撤销/不予注册上市申请、临床试验审批申请等）中监管机构已明确的相关问题。');
INSERT INTO `tbl_passage` VALUES (7348, 176, '（5）在申报前沟通中，申请人明确提出的问题，以及监管机构提供的建议。');
INSERT INTO `tbl_passage` VALUES (7349, 176, '（6）说明在本次申报中如何解决上述问题。');
INSERT INTO `tbl_passage` VALUES (7350, 176, '2.如不适用，应当明确声明申报产品没有既往申报和/或申报前沟通。');
INSERT INTO `tbl_passage` VALUES (7351, 176, '（七）符合性声明申请人应当声明下列内容：');
INSERT INTO `tbl_passage` VALUES (7352, 176, '1.申报产品符合《医疗器械注册与备案管理办法》和相关法规的要求。');
INSERT INTO `tbl_passage` VALUES (7353, 176, '2.申报产品符合《医疗器械分类规则》有关分类的要求。');
INSERT INTO `tbl_passage` VALUES (7354, 176, '3.申报产品符合现行国家标准、行业标准，并提供符合标准的清单。');
INSERT INTO `tbl_passage` VALUES (7355, 176, '4.保证所提交资料的真实性（境内产品由申请人出具，进口产品由申请人和代理人分别出具）。');
INSERT INTO `tbl_passage` VALUES (7356, 176, '二、综述资料');
INSERT INTO `tbl_passage` VALUES (7357, 176, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7358, 176, '（二）概述');
INSERT INTO `tbl_passage` VALUES (7359, 176, '1.描述申报产品的通用名称及其确定依据。');
INSERT INTO `tbl_passage` VALUES (7360, 176, '2.描述申报产品的管理类别，包括：所属分类子目录名称、一级产品类别、二级产品类别，管理类别，分类编码。');
INSERT INTO `tbl_passage` VALUES (7361, 176, '3.描述申报产品适用范围。');
INSERT INTO `tbl_passage` VALUES (7362, 176, '4.如适用，描述有关申报产品的背景信息概述或特别细节，如：申报产品的历史概述、历次提交的信息，与其他经批准上市产品的关系等。');
INSERT INTO `tbl_passage` VALUES (7363, 176, '（三）产品描述');
INSERT INTO `tbl_passage` VALUES (7364, 176, '1.器械及操作原理描述');
INSERT INTO `tbl_passage` VALUES (7365, 176, '（1）无源医疗器械描述工作原理、作用机理（如适用）、结构及组成、原材料（与使用者和/或患者直接或间接接触的材料成分；若器械中包含生物材料或衍生物，描述物质来源和原材料、预期使用目的、主要作用方式；若器械中包含活性药物成分（API）或药物，描述药物名称、预期使用目的、主要作用方式、来源）、交付状态及灭菌方式（如适用，描述灭菌实施者、灭菌方法、灭菌有效期），结构示意图和/或产品图示、使用方法及图示（如适用）以及区别于其他同类产品的特征等内容。');
INSERT INTO `tbl_passage` VALUES (7366, 176, '（2）有源医疗器械描述工作原理、作用机理（如适用）、结构及组成、主要功能及其组成部件（如关键组件和软件等）的功能、产品图示（含标识、接口、操控面板、应用部分等细节），以及区别于其他同类产品的特征等内容。含有多个组成部分的，应说明其连接或组装关系。');
INSERT INTO `tbl_passage` VALUES (7367, 176, '2.型号规格对于存在多种型号规格的产品，应当明确各型号规格的区别。应当采用对比表或带有说明性文字的图片、图表，描述各种型号规格的结构组成（或配置）、功能、产品特征和运行模式、技术参数等内容。');
INSERT INTO `tbl_passage` VALUES (7368, 176, '3.包装说明');
INSERT INTO `tbl_passage` VALUES (7369, 176, '（1）说明所有产品组成的包装信息。对于无菌医疗器械，应当说明其无菌屏障系统的信息；对于具有微生物限度要求的医疗器械，应当说明保持其微生物限度的包装信息。说明如何确保最终使用者可清晰地辨识包装的完整性。');
INSERT INTO `tbl_passage` VALUES (7370, 176, '（2）若使用者在进行灭菌前需要包装医疗器械或附件时，应当提供正确包装的信息（如材料、成分和尺寸等）。');
INSERT INTO `tbl_passage` VALUES (7371, 176, '4.研发历程阐述申请注册产品的研发背景和目的。如有参考的同类产品或前代产品，应当提供同类产品或前代产品的信息，并说明选择其作为研发参考的原因。');
INSERT INTO `tbl_passage` VALUES (7372, 176, '5.与同类和/或前代产品的参考和比较列表比较说明申报产品与同类产品和/或前代产品在工作原理、结构组成、制造材料、性能指标、作用方式（如植入、介入），以及适用范围等方面的异同。');
INSERT INTO `tbl_passage` VALUES (7373, 176, '（四）适用范围和禁忌证');
INSERT INTO `tbl_passage` VALUES (7374, 176, '1.适用范围');
INSERT INTO `tbl_passage` VALUES (7375, 176, '（1）应当明确申报产品可提供的治疗或诊断功能，可描述其医疗过程（如体内或体外诊断、康复治疗监测、避孕、消毒等），并写明申报产品诊断、治疗、预防、缓解或治愈的疾病或病况，将要监测的参数和其他与适用范围相关的考虑。');
INSERT INTO `tbl_passage` VALUES (7376, 176, '（2）申报产品的预期用途，并描述其适用的医疗阶段（如治疗后的监测、康复等）。');
INSERT INTO `tbl_passage` VALUES (7377, 176, '（3）明确目标用户及其操作或使用该产品应当具备的技能/知识/培训。');
INSERT INTO `tbl_passage` VALUES (7378, 176, '（4）说明产品是一次性使用还是重复使用。');
INSERT INTO `tbl_passage` VALUES (7379, 176, '（5）说明与其组合使用实现预期用途的其他产品。');
INSERT INTO `tbl_passage` VALUES (7380, 176, '2.预期使用环境');
INSERT INTO `tbl_passage` VALUES (7381, 176, '（1）该产品预期使用的地点，如医疗机构、实验室、救护车、家庭等。');
INSERT INTO `tbl_passage` VALUES (7382, 176, '（2）可能影响其安全性和有效性的环境条件，如温度、湿度、压力、移动、振动、海拔等。');
INSERT INTO `tbl_passage` VALUES (7383, 176, '3.适用人群目标患者人群的信息（如成人、新生儿、婴儿或者儿童）或无预期治疗特定人群的声明，患者选择标准的信息，以及使用过程中需要监测的参数、考虑的因素。如申报产品目标患者人群包含新生儿、婴儿或者儿童，应当描述预期使用申报产品治疗、诊断、预防、缓解或治愈疾病、病况的非成人特定群体。');
INSERT INTO `tbl_passage` VALUES (7384, 176, '4.禁忌证如适用，通过风险/受益评估后，针对某些疾病、情况或特定的人群（如儿童、老年人、孕妇及哺乳期妇女、肝肾功能不全者），认为不推荐使用该产品，应当明确说明。');
INSERT INTO `tbl_passage` VALUES (7385, 176, '（五）申报产品上市历史如适用，应当提交申报产品的下列资料：');
INSERT INTO `tbl_passage` VALUES (7386, 176, '1.上市情况截至提交注册申请前，申报产品在各国家或地区的上市批准时间、销售情况。若申报产品在不同国家或地区上市时有差异（如设计、标签、技术参数等），应当逐一描述。');
INSERT INTO `tbl_passage` VALUES (7387, 176, '2.不良事件和召回如适用，应当以列表形式分别对申报产品上市后发生的不良事件、召回的发生时间以及每一种情况下申请人采取的处理和解决方案，包括主动控制产品风险的措施，向医疗器械不良事件监测技术机构报告的情况，相关部门的调查处理情况等进行描述。同时，应当对上述不良事件、召回进行分析评价，阐明不良事件、召回发生的原因并对其安全性、有效性的影响予以说明。若不良事件、召回数量大，应当根据事件类型总结每个类型涉及的数量。');
INSERT INTO `tbl_passage` VALUES (7388, 176, '3.销售、不良事件及召回率如适用，应当提交申报产品近五年在各国家（地区）销售数量的总结，按以下方式提供在各国家（地区）的不良事件、召回比率，并进行比率计算关键分析。如：不良事件发生率＝不良事件数量÷销售数量×100%，召回发生率＝召回数量÷销售数量×100%。发生率可以采用每使用患者年或每使用进行计算，申请人应当描述发生率计算方法。');
INSERT INTO `tbl_passage` VALUES (7389, 176, '（六）其他需说明的内容');
INSERT INTO `tbl_passage` VALUES (7390, 176, '1.如适用，明确与申报产品联合使用实现预期用途的其他产品的详细信息。');
INSERT INTO `tbl_passage` VALUES (7391, 176, '2.对于已获得批准的部件或配合使用的附件，应当提供注册证编号和国家药监局官方网站公布的注册证信息。');
INSERT INTO `tbl_passage` VALUES (7392, 176, '三、非临床资料');
INSERT INTO `tbl_passage` VALUES (7393, 176, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7394, 176, '（二）产品风险管理资料产品风险管理资料是对产品的风险管理过程及其评审的结果予以记录所形成的资料。应当提供下列内容，并说明对于每项已判定危害的下列各个过程的可追溯性。');
INSERT INTO `tbl_passage` VALUES (7395, 176, '1.风险分析：包括医疗器械适用范围和与安全性有关特征的识别、危害的识别、估计每个危害处境的风险。');
INSERT INTO `tbl_passage` VALUES (7396, 176, '2.风险评价：对于每个已识别的危害处境，评价和决定是否需要降低风险，若需要，描述如何进行相应风险控制。');
INSERT INTO `tbl_passage` VALUES (7397, 176, '3.风险控制：描述为降低风险所执行风险控制的相关内容。');
INSERT INTO `tbl_passage` VALUES (7398, 176, '4.任何一个或多个剩余风险的可接受性评定。');
INSERT INTO `tbl_passage` VALUES (7399, 176, '5.与产品受益相比，综合评价产品风险可接受。');
INSERT INTO `tbl_passage` VALUES (7400, 176, '（三）医疗器械安全和性能基本原则清单说明产品符合《医疗器械安全和性能基本原则清单》（见附件9）各项适用要求所采用的方法，以及证明其符合性的文件。对于《医疗器械安全和性能基本原则清单》中不适用的各项要求，应当说明理由。对于包含在产品注册申报资料中的文件，应当说明其在申报资料中的具体位置；对于未包含在产品注册申报资料中的文件，应当注明该证据文件名称及其在质量管理体系文件中的编号备查。');
INSERT INTO `tbl_passage` VALUES (7401, 176, '（四）产品技术要求及检验报告');
INSERT INTO `tbl_passage` VALUES (7402, 176, '1.申报产品适用标准情况申报产品应当符合适用的强制性标准。对于强制性行业标准，若申报产品结构特征、预期用途、使用方式等与强制性标准的适用范围不一致，申请人应当提出不适用强制性标准的说明，并提供经验证的证明性资料。');
INSERT INTO `tbl_passage` VALUES (7403, 176, '2.产品技术要求医疗器械产品技术要求应当按照相关要求的规定编制。');
INSERT INTO `tbl_passage` VALUES (7404, 176, '3.产品检验报告可提交以下任一形式的检验报告：');
INSERT INTO `tbl_passage` VALUES (7405, 176, '（1）申请人出具的自检报告。');
INSERT INTO `tbl_passage` VALUES (7406, 176, '（2）委托有资质的医疗器械检验机构出具的检验报告。');
INSERT INTO `tbl_passage` VALUES (7407, 176, '（五）研究资料根据申报产品适用范围和技术特征，提供非临床研究综述，逐项描述所开展的研究，概述研究方法和研究结论。根据非临床研究综述，提供相应的研究资料，各项研究可通过文献研究、实验室研究、模型研究等方式开展，一般应当包含研究方案、研究报告。采用建模研究的，应当提供产品建模研究资料。');
INSERT INTO `tbl_passage` VALUES (7408, 176, '1.化学和物理性能研究');
INSERT INTO `tbl_passage` VALUES (7409, 176, '（1）应当提供产品化学/材料表征、物理和/或机械性能指标的确定依据、设计输入来源以及临床意义，所采用的标准或方法、采用的原因及理论基础。');
INSERT INTO `tbl_passage` VALUES (7410, 176, '（2）燃爆风险对于暴露于易燃、易爆物质或与其他可燃物、致燃物联合使用的医疗器械，应当提供燃爆风险研究资料，证明在正常状态及单一故障状态下，燃爆风险可接受。');
INSERT INTO `tbl_passage` VALUES (7411, 176, '（3）联合使用如申报产品预期与其他医疗器械、药品、非医疗器械产品联合使用实现同一预期用途，应当提供证明联合使用安全有效的研究资料，包括互联基本信息（连接类型、接口、协议、最低性能）、联合使用风险及控制措施、联合使用上的限制，兼容性研究等。联合药物使用的，应当提供药物相容性研究资料，证明药品和器械联合使用的性能符合其适应证和预期用途。');
INSERT INTO `tbl_passage` VALUES (7412, 176, '（4）量效关系和能量安全对于向患者提供能量或物质治疗的医疗器械，应当提供量效关系和能量安全性研究资料，提供证明治疗参数设置的安全性、有效性、合理性，以及除预期靶组织外，能量不会对正常组织造成不可接受的伤害的研究资料。');
INSERT INTO `tbl_passage` VALUES (7413, 176, '2.电气系统安全性研究应当提供电气安全性、机械和环境保护以及电磁兼容性的研究资料，说明适用的标准以及开展的研究。');
INSERT INTO `tbl_passage` VALUES (7414, 176, '3.辐射安全研究对于具有辐射或潜在辐射危害（包括电离辐射和非电离辐射）的产品，应当提供辐射安全的研究资料，包括：');
INSERT INTO `tbl_passage` VALUES (7415, 176, '（1）说明符合的辐射安全通用及专用标准,对于标准中的不适用条款应详细说明理由；');
INSERT INTO `tbl_passage` VALUES (7416, 176, '（2）说明辐射的类型并提供辐射安全验证资料，应确保辐射能量、辐射分布以及其他辐射关键特性能够得到合理的控制和调整，并可在使用过程中进行预估、监控。（如适用）');
INSERT INTO `tbl_passage` VALUES (7417, 176, '（3）提供减少使用者、他人和患者在运输、贮存、安装、使用中辐射吸收剂量的防护措施，避免误用的方法。对于需要安装的产品，应当明确有关验收和性能测试、验收标准及维护程序的信息。');
INSERT INTO `tbl_passage` VALUES (7418, 176, '4.软件研究');
INSERT INTO `tbl_passage` VALUES (7419, 176, '（1）软件含有软件组件的产品和独立软件，应当提供软件的研究资料，包括基本信息、实现过程、核心功能、结论等内容，详尽程度取决于软件安全性级别（严重、中等、轻微）。其中，基本信息包括软件标识、安全性级别、结构功能、物理拓扑、运行环境、注册历史，实现过程包括开发概况、风险管理、需求规范、生存周期、验证与确认、可追溯性分析、缺陷管理、更新历史，明确核心功能、核心算法、预期用途的对应关系。');
INSERT INTO `tbl_passage` VALUES (7420, 176, '（2）网络安全具备电子数据交换、远程控制或用户访问功能的独立软件和含有软件组件的产品，应当提供网络安全研究资料，包括基本信息、实现过程、漏洞评估、结论等内容，详尽程度取决于软件安全性级别。其中，基本信息包括软件信息、数据架构、网络安全能力、网络安全补丁、安全软件，实现过程包括风险管理、需求规范、验证与确认、可追溯性分析、更新维护计划，漏洞评估明确已知漏洞相关信息。');
INSERT INTO `tbl_passage` VALUES (7421, 176, '（3）现成软件产品若使用现成软件，应当根据现成软件的类型、使用方式等情况提供相应软件研究资料和网络安全研究资料。');
INSERT INTO `tbl_passage` VALUES (7422, 176, '（4）人工智能产品若采用深度学习等人工智能技术实现预期功能与用途，应当提供算法研究资料，包括算法基本信息、数据收集、算法训练、算法性能评估等内容。');
INSERT INTO `tbl_passage` VALUES (7423, 176, '（5）互操作性产品若通过电子接口与其他医疗器械或非医疗器械交换并使用信息，应当提供互操作性研究资料，包括基本信息、需求规范、风险管理、验证与确认、维护计划等内容。');
INSERT INTO `tbl_passage` VALUES (7424, 176, '（6）其他产品若采用移动计算、云计算、虚拟现实等信息通信技术实现预期功能与用途，应当提供相应技术研究资料，包括基本信息、需求规范、风险管理、验证与确认、维护计划等内容。');
INSERT INTO `tbl_passage` VALUES (7425, 176, '5.生物学特性研究对于与患者直接或间接接触的器械，应当进行生物学评价。生物学评价资料应当包括：');
INSERT INTO `tbl_passage` VALUES (7426, 176, '（1）描述产品所用材料及与人体接触性质，设计和生产过程中可能引入的污染物和残留物，设计和生产过程中可能产生的析出物（包括滤沥物和/或蒸发物）、降解产物、加工残留物，与医疗器械直接接触的包装材料等相关信息。');
INSERT INTO `tbl_passage` VALUES (7427, 176, '（2）描述申报产品的物理和/或化学信息并考虑材料表征（如适用），如器械的物理作用可能产生生物学风险，应当进行评价。');
INSERT INTO `tbl_passage` VALUES (7428, 176, '（3）生物学评价的策略、依据和方法。');
INSERT INTO `tbl_passage` VALUES (7429, 176, '（4）已有数据和结果的评价。');
INSERT INTO `tbl_passage` VALUES (7430, 176, '（5）选择或豁免生物学试验的理由和论证。');
INSERT INTO `tbl_passage` VALUES (7431, 176, '（6）完成生物学评价所需的其他数据。若医疗器械材料可能释放颗粒进入患者和使用者体内，从而产生与颗粒尺寸和性质相关风险，如纳米材料，对所有包含、产生或由其组成的医疗器械，应当提供相关生物学风险研究资料。若根据申报产品预期用途，其会被人体吸收、代谢，如可吸收产品，应当提供所用材料/物质与人体组织、细胞和体液之间相容性的研究资料。');
INSERT INTO `tbl_passage` VALUES (7432, 176, '6.生物源材料的安全性研究对于含有同种异体材料、动物源性材料或生物活性物质等具有生物安全风险的产品，应当提供相应生物安全性研究资料。生物安全性研究资料应当包括：');
INSERT INTO `tbl_passage` VALUES (7433, 176, '（1）相应材料或物质的情况，组织、细胞和材料的获取、加工、保存、测试和处理过程。');
INSERT INTO `tbl_passage` VALUES (7434, 176, '（2）阐述来源，并说明生产过程中灭活和去除病毒和/或传染性因子的工艺过程，提供有效性验证数据或相关资料。');
INSERT INTO `tbl_passage` VALUES (7435, 176, '（3）说明降低免疫原性物质的方法和/或工艺过程，提供质量控制指标与验证性实验数据或相关资料。');
INSERT INTO `tbl_passage` VALUES (7436, 176, '（4）支持生物源材料安全性的其他资料。');
INSERT INTO `tbl_passage` VALUES (7437, 176, '7.清洁、消毒、灭菌研究');
INSERT INTO `tbl_passage` VALUES (7438, 176, '（1）生产企业灭菌：应当明确灭菌工艺（方法和参数）和无菌保证水平（SAL），并提供灭菌验证及确认的相关研究资料。');
INSERT INTO `tbl_passage` VALUES (7439, 176, '（2）使用者灭菌：应当明确推荐的灭菌工艺（方法和参数）、所推荐灭菌工艺的确定依据以及验证的相关研究资料；对可耐受两次或多次灭菌的产品，应当提供产品所推荐灭菌工艺耐受性的研究资料。');
INSERT INTO `tbl_passage` VALUES (7440, 176, '（3）使用者清洁和消毒：应当明确推荐的清洗和消毒工艺（方法和参数）、工艺的确定依据以及验证的相关研究资料。');
INSERT INTO `tbl_passage` VALUES (7441, 176, '（4）残留毒性：若产品经灭菌或消毒后可能产生残留物质,应当对灭菌或消毒后的产品进行残留毒性的研究，明确残留物信息及采取的处理方法，并提供相关研究资料。');
INSERT INTO `tbl_passage` VALUES (7442, 176, '（5）以非无菌状态交付，且使用前需灭菌的医疗器械，应当提供证明包装能减少产品受到微生物污染的风险，且适用于生产企业规定灭菌方法的研究资料。');
INSERT INTO `tbl_passage` VALUES (7443, 176, '8.动物试验研究为避免开展不必要的动物试验，医疗器械是否开展动物试验研究应当进行科学决策，并提供论证/说明资料。经决策需通过动物试验研究验证/确认产品风险控制措施有效性的，应当提供动物试验研究资料，研究资料应当包括试验目的、实验动物信息、受试器械和对照信息、动物数量、评价指标和试验结果、动物试验设计要素的确定依据等内容。');
INSERT INTO `tbl_passage` VALUES (7444, 176, '9.证明产品安全性、有效性的其他研究资料。');
INSERT INTO `tbl_passage` VALUES (7445, 176, '（六）非临床文献提供与申报产品相关的已发表的非临床研究（如尸体研究、生物力学研究等）文献/书目列表，并提供相关内容的复印件（外文应同时提供翻译件）。如未检索到与申报产品相关的非临床文献/书目，应当提供相关的声明。');
INSERT INTO `tbl_passage` VALUES (7446, 176, '（七）稳定性研究');
INSERT INTO `tbl_passage` VALUES (7447, 176, '1.货架有效期如适用，应当提供货架有效期和包装研究资料，证明在货架有效期内，在生产企业规定的运输贮存条件下，产品可保持性能功能满足使用要求，具有微生物限度要求的产品还应当符合微生物限度要求，以无菌状态交付的产品还应保持无菌状态。');
INSERT INTO `tbl_passage` VALUES (7448, 176, '2.使用稳定性如适用，应当提供使用稳定性/可靠性研究资料，证明在生产企业规定的使用期限/使用次数内，在正常使用、维护和校准（如适用）情况下，产品的性能功能满足使用要求。');
INSERT INTO `tbl_passage` VALUES (7449, 176, '3.运输稳定性应当提供运输稳定性和包装研究资料，证明在生产企业规定的运输条件下，运输过程中的环境条件（例如：震动、振动、温度和湿度的波动）不会对医疗器械的特性和性能，包括完整性和清洁度，造成不利影响。');
INSERT INTO `tbl_passage` VALUES (7450, 176, '（八）其他资料免于进行临床评价的第二类、第三类医疗器械，申请人应当按照《列入免于进行临床评价医疗器械目录产品对比说明技术指导原则》，从基本原理、结构组成、性能、安全性、适用范围等方面，证明产品的安全有效性。对于一次性使用的医疗器械，还应当提供证明其无法重复使用的支持性资料。');
INSERT INTO `tbl_passage` VALUES (7451, 176, '四、临床评价资料需要进行临床评价的第二类、第三类医疗器械，按照相关要求提供临床评价资料。');
INSERT INTO `tbl_passage` VALUES (7452, 176, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7453, 176, '（二）临床评价资料');
INSERT INTO `tbl_passage` VALUES (7454, 176, '1.产品描述和研发背景：包括申报产品基本信息、适用范围、现有的诊断或治疗方法及涉及医疗器械的临床应用情况、申报产品与现有诊断或治疗方法的关系、预期达到的临床疗效等。');
INSERT INTO `tbl_passage` VALUES (7455, 176, '2.明确临床评价涵盖的范围，申报产品中如有可免于进行临床评价的部分，描述其结构组成并说明免于进行临床评价的理由。');
INSERT INTO `tbl_passage` VALUES (7456, 176, '3.临床评价路径：根据申报产品的适用范围、技术特征、已有临床数据等具体情况，选择恰当的临床评价路径，包括同品种临床评价路径和/或临床试验路径。');
INSERT INTO `tbl_passage` VALUES (7457, 176, '4.若通过同品种临床评价路径进行临床评价，应当提交申报产品与同品种医疗器械在适用范围、技术特征、生物学特性方面的对比资料；应当对同品种医疗器械的临床数据进行收集、评估和分析，形成临床证据。如适用，应当描述申报产品与同品种医疗器械的差异，提交充分的科学证据证明二者具有相同的安全有效性。');
INSERT INTO `tbl_passage` VALUES (7458, 176, '5.若通过临床试验路径进行临床评价，应当提交临床试验方案、临床试验机构伦理委员会同意开展临床试验的书面意见、临床试验报告、知情同意书样本，并附临床试验数据库（原始数据库、分析数据库、说明性文件和程序代码）。');
INSERT INTO `tbl_passage` VALUES (7459, 176, '（三）其他资料如适用，提供相应项目评价资料的摘要、报告和数据。');
INSERT INTO `tbl_passage` VALUES (7460, 176, '五、产品说明书和标签样稿');
INSERT INTO `tbl_passage` VALUES (7461, 176, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7462, 176, '（二）产品说明书');
INSERT INTO `tbl_passage` VALUES (7463, 176, '1.应当提交产品说明书，内容应当符合《医疗器械说明书和标签管理规定》和相关法规、规章、规范性文件、强制性标准的要求。');
INSERT INTO `tbl_passage` VALUES (7464, 176, '2.境外申请人应当提交产品原文说明书。');
INSERT INTO `tbl_passage` VALUES (7465, 176, '（三）标签样稿应当提交最小销售单元标签样稿，内容应当符合《医疗器械说明书和标签管理规定》和相关法规、规章、规范性文件、强制性标准的要求。');
INSERT INTO `tbl_passage` VALUES (7466, 176, '（四）其他资料如适用，提交对产品信息进行补充说明的其他文件。');
INSERT INTO `tbl_passage` VALUES (7467, 176, '六、质量管理体系文件');
INSERT INTO `tbl_passage` VALUES (7468, 176, '（一）综述申请人应当承诺已按照相关法规要求建立相应的质量管理体系，随时接受质量管理体系核查。');
INSERT INTO `tbl_passage` VALUES (7469, 176, '（二）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7470, 176, '（三）生产制造信息');
INSERT INTO `tbl_passage` VALUES (7471, 176, '1.产品描述信息器械工作原理和总体生产工艺的简要说明。');
INSERT INTO `tbl_passage` VALUES (7472, 176, '2.一般生产信息提供生产器械或其部件的所有地址和联络信息。如适用，应当提供外包生产、重要组件或原材料的生产（如动物组织和药品）、关键工艺过程、灭菌等情况的所有重要供应商名称和地址。');
INSERT INTO `tbl_passage` VALUES (7473, 176, '（四）质量管理体系程序用于建立和维护质量管理体系的高层级质量管理体系程序，包括质量手册、质量方针、质量目标和文件及记录控制程序。');
INSERT INTO `tbl_passage` VALUES (7474, 176, '（五）管理职责程序用于通过阐述质量方针、策划、职责/权限/沟通和管理评审，对建立和维护质量管理体系形成管理保证文件的程序。');
INSERT INTO `tbl_passage` VALUES (7475, 176, '（六）资源管理程序用于为实施和维护质量管理体系所形成足够资源（包括人力资源、基础设施和工作环境）供应文件的程序。');
INSERT INTO `tbl_passage` VALUES (7476, 176, '（七）产品实现程序高层级的产品实现程序，如说明策划和客户相关过程的程序。');
INSERT INTO `tbl_passage` VALUES (7477, 176, '1.设计和开发程序用于形成从项目初始至设计转换的整个过程中关于产品设计的系统性和受控的开发过程文件的程序。');
INSERT INTO `tbl_passage` VALUES (7478, 176, '2.采购程序用于形成符合已制定的质量和/或产品技术参数的采购产品/服务文件的程序。');
INSERT INTO `tbl_passage` VALUES (7479, 176, '3.生产和服务控制程序用于形成受控条件下生产和服务活动文件的程序，这些程序阐述诸如产品的清洁和污染的控制、安装和服务活动、过程确认、标识和可追溯性等问题。');
INSERT INTO `tbl_passage` VALUES (7480, 176, '4.监视和测量装置控制程序用于形成质量管理体系运行过程中所使用的监视和测量设备已受控并持续符合既定要求文件的程序。');
INSERT INTO `tbl_passage` VALUES (7481, 176, '（八）质量管理体系的测量、分析和改进程序用于形成如何监视、测量、分析和改进以确保产品和质量管理体系的符合性，并保持质量管理体系有效性的文件的程序。');
INSERT INTO `tbl_passage` VALUES (7482, 176, '（九）其他质量体系程序信息不属于上述内容，但对此次申报较为重要的其他信息。');
INSERT INTO `tbl_passage` VALUES (7483, 176, '（十）质量管理体系核查文件根据上述质量管理体系程序，申请人应当形成相关质量管理体系文件和记录。应当提交下列资料，在质量管理体系核查时进行检查。');
INSERT INTO `tbl_passage` VALUES (7484, 176, '1.申请人基本情况表。');
INSERT INTO `tbl_passage` VALUES (7485, 176, '2.申请人组织机构图。');
INSERT INTO `tbl_passage` VALUES (7486, 176, '3.生产企业总平面布置图、生产区域分布图。');
INSERT INTO `tbl_passage` VALUES (7487, 176, '4.生产过程有净化要求的，应当提供有资质的检测机构出具的环境检测报告（附平面布局图）复印件。');
INSERT INTO `tbl_passage` VALUES (7488, 176, '5.产品生产工艺流程图，应当标明主要控制点与项目及主要原材料、采购件的来源及质量控制方法。');
INSERT INTO `tbl_passage` VALUES (7489, 176, '6.主要生产设备和检验设备（包括进货检验、过程检验、出厂最终检验所需的相关设备；在净化条件下生产的，还应当提供环境监测设备）目录。');
INSERT INTO `tbl_passage` VALUES (7490, 176, '7.质量管理体系自查报告。');
INSERT INTO `tbl_passage` VALUES (7491, 176, '8.如适用，应当提供拟核查产品与既往已通过核查产品在生产条件、生产工艺等方面的对比说明。');
INSERT INTO `tbl_passage` VALUES (7492, 177, '附件7医疗器械变更备案/变更注册申报资料要求及说明变更备案资料要求及说明');
INSERT INTO `tbl_passage` VALUES (7493, 177, '一、监管信息');
INSERT INTO `tbl_passage` VALUES (7494, 177, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7495, 177, '（二）备案表按照填表要求填写。');
INSERT INTO `tbl_passage` VALUES (7496, 177, '（三）关联文件');
INSERT INTO `tbl_passage` VALUES (7497, 177, '1.境内注册人应当提交：企业营业执照副本或事业单位法人证书复印件。');
INSERT INTO `tbl_passage` VALUES (7498, 177, '2.境外注册人应当提交：');
INSERT INTO `tbl_passage` VALUES (7499, 177, '（1）根据变更事项在境外注册人注册地或生产地所在国家（地区）是否需要获得新的企业资格证明文件，提交相应的企业资格证明文件。');
INSERT INTO `tbl_passage` VALUES (7500, 177, '（2）如变更事项在境外注册人注册地或生产地所在国家（地区），需要获得新的医疗器械主管部门出具的准许产品上市销售证明文件，应当提交相应文件；如变更事项不需要获得注册人注册地或生产地址所在国家（地区）医疗器械主管部门批准的，应当予以说明；未在境外上市的创新医疗器械可以不提交。');
INSERT INTO `tbl_passage` VALUES (7501, 177, '（3）在中国境内指定代理人的委托书、代理人承诺书及营业执照副本复印件。');
INSERT INTO `tbl_passage` VALUES (7502, 177, '3.应当提交原医疗器械注册证及其附件的复印件、历次医疗器械变更注册（备案）文件及其附件的复印件。');
INSERT INTO `tbl_passage` VALUES (7503, 177, '（四）符合性声明注册人应当声明下列内容：');
INSERT INTO `tbl_passage` VALUES (7504, 177, '1.申报产品符合《医疗器械注册与备案管理办法》和相关法规的要求；');
INSERT INTO `tbl_passage` VALUES (7505, 177, '2.申报产品符合《医疗器械分类规则》有关分类的要求。');
INSERT INTO `tbl_passage` VALUES (7506, 177, '3.申报产品符合现行国家标准、行业标准，并提供符合标准的清单。');
INSERT INTO `tbl_passage` VALUES (7507, 177, '4.保证所提交资料的真实性（境内产品由注册人出具，进口产品由注册人和代理人分别出具）。');
INSERT INTO `tbl_passage` VALUES (7508, 177, '二、综述资料');
INSERT INTO `tbl_passage` VALUES (7509, 177, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。产品描述');
INSERT INTO `tbl_passage` VALUES (7510, 177, '1.注册人关于变更情况的说明。详细描述本次变更情况、变更的具体原因及目的。');
INSERT INTO `tbl_passage` VALUES (7511, 177, '2.根据产品具体变更情况提供相应文件，包括下列情形：');
INSERT INTO `tbl_passage` VALUES (7512, 177, '（1）注册人名称变更企业名称变更核准通知书（境内注册人）和/或相应关联文件。');
INSERT INTO `tbl_passage` VALUES (7513, 177, '（2）注册人住所变更变更前后企业营业执照副本或事业单位法人证书复印件（境内注册人）和/或相应关联文件。');
INSERT INTO `tbl_passage` VALUES (7514, 177, '（3）境内医疗器械生产地址变更变更后的生产许可证及其附件。');
INSERT INTO `tbl_passage` VALUES (7515, 177, '（4）代理人变更①注册人出具新代理人委托书、新代理人出具的承诺书；②新代理人的营业执照副本复印件。');
INSERT INTO `tbl_passage` VALUES (7516, 177, '（5）代理人住所变更变更前后营业执照副本复印件。\n变更注册申报资料要求及说明');
INSERT INTO `tbl_passage` VALUES (7517, 177, '一、监管信息');
INSERT INTO `tbl_passage` VALUES (7518, 177, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7519, 177, '（二）申请表按照填表要求填写。');
INSERT INTO `tbl_passage` VALUES (7520, 177, '（三）关联文件');
INSERT INTO `tbl_passage` VALUES (7521, 177, '1.境内注册人应当提交：企业营业执照副本或事业单位法人证书复印件。');
INSERT INTO `tbl_passage` VALUES (7522, 177, '2.境外注册人应当提交：');
INSERT INTO `tbl_passage` VALUES (7523, 177, '（1）根据变更事项在境外注册人注册地或生产地所在国家（地区）是否需要获得新的企业资格证明文件，提交相应的企业资格证明文件。');
INSERT INTO `tbl_passage` VALUES (7524, 177, '（2）如变更事项在境外注册人注册地或生产地所在国家（地区），需要获得新的医疗器械主管部门出具的准许产品上市销售证明文件，应当提交相应文件；如变更事项不需要获得注册人注册地或生产地址所在国家（地区）医疗器械主管部门批准的，应当予以说明；未在境外上市的创新医疗器械可以不提交。');
INSERT INTO `tbl_passage` VALUES (7525, 177, '（3）在中国境内指定代理人的委托书、代理人承诺书及营业执照副本复印件。');
INSERT INTO `tbl_passage` VALUES (7526, 177, '3.应当提交原医疗器械注册证及其附件的复印件、历次医疗器械变更注册（备案）文件及其附件的复印件。');
INSERT INTO `tbl_passage` VALUES (7527, 177, '（四）申报前与监管机构的联系情况和沟通记录');
INSERT INTO `tbl_passage` VALUES (7528, 177, '1.在本次变更申请提交前，如注册人与监管机构针对申报产品以会议形式进行了沟通，应当提供下列内容（如适用）：');
INSERT INTO `tbl_passage` VALUES (7529, 177, '（1）列出监管机构回复的沟通情况。');
INSERT INTO `tbl_passage` VALUES (7530, 177, '（2）在沟通中，注册人明确提出的问题，及监管机构提供的建议。');
INSERT INTO `tbl_passage` VALUES (7531, 177, '（3）说明在本次申报中如何解决上述问题。');
INSERT INTO `tbl_passage` VALUES (7532, 177, '2.如不适用，应当明确声明申报产品没有既往申报和/或申报前沟通。');
INSERT INTO `tbl_passage` VALUES (7533, 177, '（五）符合性声明注册人应当声明下列内容：');
INSERT INTO `tbl_passage` VALUES (7534, 177, '1.申报产品符合《医疗器械注册与备案管理办法》和相关法规的要求。');
INSERT INTO `tbl_passage` VALUES (7535, 177, '2.申报产品符合《医疗器械分类规则》有关分类的要求。');
INSERT INTO `tbl_passage` VALUES (7536, 177, '3.申报产品符合现行国家标准、行业标准，并提供符合标准的清单。');
INSERT INTO `tbl_passage` VALUES (7537, 177, '4.保证所提交资料的真实性（境内产品由注册人出具，进口产品由注册人和代理人分别出具）。');
INSERT INTO `tbl_passage` VALUES (7538, 177, '二、综述资料');
INSERT INTO `tbl_passage` VALUES (7539, 177, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7540, 177, '（二）概述详细描述本次变更情况、变更的具体原因及目的。');
INSERT INTO `tbl_passage` VALUES (7541, 177, '（三）产品变更情况描述根据产品具体变更情况提供相应的说明及对比表，包括下列情形：');
INSERT INTO `tbl_passage` VALUES (7542, 177, '1.产品名称变化。');
INSERT INTO `tbl_passage` VALUES (7543, 177, '2.产品技术要求变化。');
INSERT INTO `tbl_passage` VALUES (7544, 177, '3.型号、规格变化。');
INSERT INTO `tbl_passage` VALUES (7545, 177, '4.结构及组成变化。');
INSERT INTO `tbl_passage` VALUES (7546, 177, '5.产品适用范围变化。');
INSERT INTO `tbl_passage` VALUES (7547, 177, '6.进口医疗器械生产地址变化。');
INSERT INTO `tbl_passage` VALUES (7548, 177, '7.注册证中“其他内容”变化。');
INSERT INTO `tbl_passage` VALUES (7549, 177, '8.其他变化。');
INSERT INTO `tbl_passage` VALUES (7550, 177, '三、非临床资料');
INSERT INTO `tbl_passage` VALUES (7551, 177, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7552, 177, '（二）产品风险管理资料应当提交与产品变化相关的产品风险管理资料。产品风险管理资料是对产品的风险管理过程及其评审的结果予以记录所形成的资料。应当提供如下内容，并说明对于每项已判定危害的下列各个过程的可追溯性。');
INSERT INTO `tbl_passage` VALUES (7553, 177, '1.风险分析：包括医疗器械适用范围和与安全性有关特征的识别、危害的识别、估计每个危害处境的风险。');
INSERT INTO `tbl_passage` VALUES (7554, 177, '2.风险评价：对于每个已识别的危害处境，评价和决定是否需要降低风险，若需要，描述如何进行相应风险控制。');
INSERT INTO `tbl_passage` VALUES (7555, 177, '3.风险控制：描述为降低风险所执行风险控制的相关内容。');
INSERT INTO `tbl_passage` VALUES (7556, 177, '4.任何一个或多个剩余风险的可接受性评定。');
INSERT INTO `tbl_passage` VALUES (7557, 177, '5.与产品受益相比，综合评价产品风险可接受。');
INSERT INTO `tbl_passage` VALUES (7558, 177, '（三）产品技术要求及检验报告如适用，应当提交下列资料：');
INSERT INTO `tbl_passage` VALUES (7559, 177, '1.申报产品适用标准情况申报产品应当符合适用的强制性标准。对于强制性行业标准，若申报产品结构特征、预期用途、使用方式等与强制性标准的适用范围不一致，注册人应当提出不适用强制性标准的说明，并提供经验证的证明性资料。');
INSERT INTO `tbl_passage` VALUES (7560, 177, '2.产品技术要求由于医疗器械强制性标准已经修订或者其他变化，涉及产品技术要求变化的，应当明确产品技术要求变化的具体内容。');
INSERT INTO `tbl_passage` VALUES (7561, 177, '3.产品检验报告可提交以下任一形式的针对产品技术要求变化部分的检验报告：');
INSERT INTO `tbl_passage` VALUES (7562, 177, '（1）注册人出具的自检报告。');
INSERT INTO `tbl_passage` VALUES (7563, 177, '（2）委托有资质的医疗器械检验机构出具的检验报告。');
INSERT INTO `tbl_passage` VALUES (7564, 177, '（四）研究资料');
INSERT INTO `tbl_passage` VALUES (7565, 177, '1.分析并说明变化部分对产品安全性、有效性的影响。');
INSERT INTO `tbl_passage` VALUES (7566, 177, '2.根据变化情况，提供非临床研究综述，逐项描述所开展的研究，概述研究方法和研究结论。根据非临床研究综述，提供相应的研究资料，各项研究资料一般应当包含研究方案、研究报告。');
INSERT INTO `tbl_passage` VALUES (7567, 177, '（五）其他资料免于进行临床评价的第二类、第三类医疗器械，如发生前文所述的变化，有可能影响产品安全、有效及申报产品与《免于进行临床评价医疗器械目录》所述产品等同性论证的，申请人应当按照《列入免于进行临床评价医疗器械目录产品对比说明技术指导原则》，从基本原理、结构组成、性能、安全性、适用范围等方面，证明产品的安全有效性。');
INSERT INTO `tbl_passage` VALUES (7568, 177, '四、临床评价资料需要进行临床评价的第二类、第三类医疗器械，如发生前文所述的变化，有可能影响产品安全、有效的，涉及临床评价的，应当按照相关要求提供适用的临床评价资料。');
INSERT INTO `tbl_passage` VALUES (7569, 177, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7570, 177, '（二）临床评价资料要求');
INSERT INTO `tbl_passage` VALUES (7571, 177, '1.产品描述和研发背景：包括申报产品基本信息、适用范围、现有的诊断或治疗方法及涉及医疗器械的临床应用情况、申报产品与现有诊断或治疗方法的关系、预期达到的临床疗效等。');
INSERT INTO `tbl_passage` VALUES (7572, 177, '2.明确临床评价涵盖的范围，申报产品中如有可免于进行临床评价的部分，描述其结构组成并说明免于进行临床评价的理由。');
INSERT INTO `tbl_passage` VALUES (7573, 177, '3.临床评价路径：根据申报产品的适用范围、技术特征、已有临床数据等具体情况，选择恰当的临床评价路径，包括同品种临床评价路径和/或临床试验路径。');
INSERT INTO `tbl_passage` VALUES (7574, 177, '4.若通过同品种临床评价路径进行临床评价，应当提交申报产品与同品种医疗器械在适用范围、技术特征、生物学特性方面的对比资料；应当对同品种医疗器械的临床数据进行收集、评估和分析，形成临床证据。如适用，应当描述申报产品与同品种医疗器械的差异，提交充分的科学证据证明二者具有相同的安全有效性。');
INSERT INTO `tbl_passage` VALUES (7575, 177, '5.若通过临床试验路径进行临床评价，应当提交临床试验方案、临床试验机构伦理委员会同意开展临床试验的书面意见、临床试验报告、知情同意书样本，并附临床试验数据库（原始数据库、分析数据库、说明性文件和程序代码）。');
INSERT INTO `tbl_passage` VALUES (7576, 177, '（三）其他资料如适用，提供相应项目评价资料的摘要、报告和数据。');
INSERT INTO `tbl_passage` VALUES (7577, 177, '五、产品说明书');
INSERT INTO `tbl_passage` VALUES (7578, 177, '（一）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7579, 177, '（二）产品说明书如适用，应当以对比表形式详细说明变更内容，并提交变更前的说明书以及变更后的产品说明书，产品说明书内容应当符合《医疗器械说明书和标签管理规定》和相关法规、规章、规范性文件、强制性标准的要求。如不适用，应当提供相应说明。');
INSERT INTO `tbl_passage` VALUES (7580, 177, '（三）其他资料如申报产品还有对产品信息进行补充说明的其他文件，如适用，应当以对比表形式详细说明变更内容，并提交变更前、后的文件。如不适用，应当提供相应说明。');
INSERT INTO `tbl_passage` VALUES (7581, 177, '六、质量管理体系文件已注册产品发生前述变更注册情形的，注册人应当承诺已根据产品变更的具体情况，按照相关法规要求对已建立的质量管理体系进行相应调整，并随时接受质量管理体系核查。注册人提出变更的具体原因或目的涉及产品设计、原材料、生产工艺、适用范围、使用方法变化的，应当针对变化部分进行质量管理体系核查；其余变化，一般不需进行质量管理体系核查。需要进行质量管理体系核查的，应当按照附件5的要求提交本部分资料。');
INSERT INTO `tbl_passage` VALUES (7582, 177, '（一）综述注册人应当承诺已按照相关法规要求，根据产品变更的具体情形对质量管理体系进行相应调整，随时接受质量管理体系核查。详述涉及产品变更项目的质量管理体系变化情况，并按照下列要求逐项提交适用项目的资料，不适用应当说明理由。');
INSERT INTO `tbl_passage` VALUES (7583, 177, '（二）章节目录应当包括本章的所有标题和小标题，注明目录中各内容的页码。');
INSERT INTO `tbl_passage` VALUES (7584, 177, '（三）生产制造信息');
INSERT INTO `tbl_passage` VALUES (7585, 177, '1.产品描述信息器械工作原理和总体生产工艺的简要说明。');
INSERT INTO `tbl_passage` VALUES (7586, 177, '2.一般生产信息提供生产器械或其部件的所有地址和联络信息。如适用，应当提供外包生产、重要组件或原材料的生产（如动物组织和药品）、关键工艺过程、灭菌等情况的所有重要供应商名称和地址。');
INSERT INTO `tbl_passage` VALUES (7587, 177, '（四）质量管理体系程序用于建立和维护质量管理体系的高层级质量管理体系程序，包括质量手册、质量方针、质量目标和文件及记录控制程序。');
INSERT INTO `tbl_passage` VALUES (7588, 177, '（五）管理职责程序用于通过阐述质量方针、策划、职责/权限/沟通和管理评审，对建立和维护质量管理体系形成管理保证文件的程序。');
INSERT INTO `tbl_passage` VALUES (7589, 177, '（六）资源管理程序用于为实施和维护质量管理体系所形成足够资源（包括人力资源、基础设施和工作环境）供应文件的程序。');
INSERT INTO `tbl_passage` VALUES (7590, 177, '（七）产品实现程序高层级的产品实现程序，如说明策划和客户相关过程的程序。');
INSERT INTO `tbl_passage` VALUES (7591, 177, '1.设计和开发程序用于形成从项目初始至设计转换的整个过程中关于产品设计的系统性和受控的开发过程文件的程序。');
INSERT INTO `tbl_passage` VALUES (7592, 177, '2.采购程序用于形成符合已制定的质量和/或产品技术参数的采购产品/服务文件的程序。');
INSERT INTO `tbl_passage` VALUES (7593, 177, '3.生产和服务控制程序用于形成受控条件下生产和服务活动文件的程序，这些程序阐述诸如产品的清洁和污染的控制、安装和服务活动、过程确认、标识和可追溯性等问题。');
INSERT INTO `tbl_passage` VALUES (7594, 177, '4.监视和测量装置控制程序用于形成质量管理体系运行过程中所使用的监视和测量设备已受控并持续符合既定要求的文件的程序。');
INSERT INTO `tbl_passage` VALUES (7595, 177, '（八）质量管理体系的测量、分析和改进程序用于形成如何监视、测量、分析和改进以确保产品和质量管理体系的符合性，并保持质量管理体系有效性的文件的程序。');
INSERT INTO `tbl_passage` VALUES (7596, 177, '（九）其他质量体系程序信息不属于上述内容，但对此次申报较为重要的其他信息。');
INSERT INTO `tbl_passage` VALUES (7597, 177, '（十）质量管理体系文件根据上述质量管理体系程序，注册人应当形成涉及产品变更项目的相关质量管理体系文件和记录：');
INSERT INTO `tbl_passage` VALUES (7598, 177, '1.注册人基本情况表。');
INSERT INTO `tbl_passage` VALUES (7599, 177, '2.注册人组织机构图。');
INSERT INTO `tbl_passage` VALUES (7600, 177, '3.生产企业总平面布置图、生产区域分布图。');
INSERT INTO `tbl_passage` VALUES (7601, 177, '4.如生产过程有净化要求的应当提供有资质的检测机构出具的环境检测报告（附平面布局图）复印件。');
INSERT INTO `tbl_passage` VALUES (7602, 177, '5.产品生产工艺流程图，应当标明主要控制点与项目及主要原材料、采购件的来源及质量控制方法。');
INSERT INTO `tbl_passage` VALUES (7603, 177, '6.主要生产设备和检验设备（包括进货检验、过程检验、出厂的最终检验相关设备；如需净化生产的，还应当提供环境监测设备）目录。');
INSERT INTO `tbl_passage` VALUES (7604, 177, '7.注册质量管理体系自查报告。');
INSERT INTO `tbl_passage` VALUES (7605, 177, '8.如适用，应当提供拟核查产品与既往已通过核查产品在生产条件、生产工艺等方面的对比说明。');
INSERT INTO `tbl_passage` VALUES (7606, 178, '附件3国家药品监督管理局医疗器械临床试验审批意见单（格式）受理号：审批部门：                                            批准日期： 年  月  日（审批部门盖章）');
INSERT INTO `tbl_passage` VALUES (7607, 179, '附件8体外诊断试剂临床评价立卷审查表（临床试验）使用说明：');
INSERT INTO `tbl_passage` VALUES (7608, 179, '1.本文件用于回答“体外诊断试剂注册项目立卷审查标准”和“体外诊断试剂变更注册项目立卷审查标准”中临床试验问题时使用。');
INSERT INTO `tbl_passage` VALUES (7609, 179, '2.依照注册申报资料情况对“临床评价情况”中内容进行勾选。');
INSERT INTO `tbl_passage` VALUES (7610, 179, '3.按照“临床评价情况”中勾选的情况，填写表格。通过临床试验路径进行临床评价的，应填写《体外诊断试剂临床评价立卷审查表（临床试验）》。根据适用审查表的填写情况，给出临床评价立卷审查结论。');
INSERT INTO `tbl_passage` VALUES (7611, 179, '4.立卷审查问题中临床试验相关问题，除有特殊说明外，均同时适用于境内、境外开展的临床试验资料。\n流水号：产品名称：申请人/注册人名称：临床评价情况：□免于进行临床试验路径			□临床试验路径境内临床试验数据  	□包含   □不包含境外临床试验数据  	□包含   □不包含临床评价立卷审查结论：□通过           □不通过\n\n体外诊断试剂临床评价立卷审查表（临床试验）');
INSERT INTO `tbl_passage` VALUES (7612, 180, '附件7体外诊断试剂临床评价立卷审查表（非临床试验）');
INSERT INTO `tbl_passage` VALUES (7613, 181, '附件5体外诊断试剂产品注册项目立卷审查要求相关说明：');
INSERT INTO `tbl_passage` VALUES (7614, 181, '1.为确保体外诊断试剂产品注册项目立卷审查工作的规范开展，制定本审查要求。2．立卷审查指按照立卷审查要求对申报资料进行审查,对申报资料进入技术审评环节的完整性、合规性、一致性进行判断的过程。立卷审查不对产品的安全性、有效性证明的合理性、充分性进行分析，亦不对产品风险受益比进行判定。3．对于立卷审查要求中的问题，若在立卷审查环节未能做出充分判断，导致不应通过立卷审查环节的申报资料通过了立卷审查，在技术审评环节，仍可对立卷审查要求中的问题提出补正意见。');
INSERT INTO `tbl_passage` VALUES (7615, 181, '4.本文件供审评机构用于体外诊断试剂产品注册申报资料的立卷审查。申请人在准备注册申报资料时，可依据本文件进行自查，自查表不需要在申报时提交。');
INSERT INTO `tbl_passage` VALUES (7616, 181, '5.本文件与电子申报配合使用，章节设置与电子申报保持一致。使用说明：1．产品注册立卷审查由审评部人员、临床与生物统计部人员共同完成。临床评价部分由审评部或临床与生物统计部人员完成，剩余部分由审评部人员完成。2．审评部人员、临床与生物统计部人员分别填写产品基本信息，分别开展各自负责内容的审查，并给出负责内容的审查分结论。3．审评部人员、临床与生物统计部人员对申报资料进行立卷审查时，应按照表格要求进行适当选项的勾选，并对相关内容进行填写。对于勾选“否”的项目，应在“存在问题”中给出该项判定为“否”的所有原因。存在问题将告知申请人。4．本表格中临床评价问题的回答应通过对“体外诊断试剂临床评价立卷审查表”中问题的回答得出最终结论。“体外诊断试剂临床评价立卷审查表”应作为立卷审查单的一部分进行汇总。流水号：产品名称：申请人名称：临床与生物统计部立卷审查分结论适用性：                                □适用   □不适用审评部立卷审查分结论：          □通过   □不通过临床与生物统计部立卷审查分结论：□通过   □不通过   立卷审查总结论：                □通过   □不通过');
INSERT INTO `tbl_passage` VALUES (7617, 182, '附件6体外诊断试剂变更注册项目立卷审查要求相关说明：');
INSERT INTO `tbl_passage` VALUES (7618, 182, '1.为确保体外诊断试剂产品变更注册项目立卷审查的规范开展，制定本审查要求。');
INSERT INTO `tbl_passage` VALUES (7619, 182, '2.立卷审查指按照立卷审查要求对申报资料进行审查,对申报资料进入技术审评环节的完整性、合规性、一致性进行判断的过程。立卷审查不对产品的安全性、有效性评价的合理性、充分性进行分析，亦不对产品风险受益比进行判定。');
